INSERT INTO `broadcast_text_locale` VALUES (78511, 'enUS', 'So soon? What happened to the last batch of slaves we sold you just yesterday? No matter. There are more. There are always more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78512, 'enUS', 'First the Laughing Skull are wiped out and soon these tiny dwarves at Bastion Rise. The Iron Horde are truly gifted where the waging of war is concerned.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78513, 'enUS', 'You are by far the best customers I have ever had the pleasure of dealing with. Would you like to open a line of credit, or do you prefer to pay in black rock again? Gold perhaps?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78514, 'enUS', 'More slaves!', '', 26124);
