INSERT INTO `broadcast_text_locale` VALUES (78516, 'enUS', 'Your slaves die quickly. We want better slaves, ogre!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78517, 'enUS', 'You talk too much. Less talk, more slaves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78518, 'enUS', 'Maybe we will fire the Goliath prototype at your slave pen?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78519, 'enUS', 'Your slaves cost too much already and die far too quickly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78520, 'enUS', 'All will feel the might of the Iron Horde. Even the ogres, if your slaves do not improve!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78521, 'enUS', 'Your slaves are all dead! Why would I buy more?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78522, 'enUS', 'I\'m certain we can come to a mutually beneficial arrangement.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78523, 'enUS', 'Of course, of course, my friend. No worries.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78524, 'enUS', 'No need to be upset, my friend. We will make sure you receive only the toughest slaves.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78525, 'enUS', 'Of course, you are quite right, my capable orc friend. Quite right.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78526, 'enUS', 'I shall redouble my efforts to address all of your concerns.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78527, 'enUS', 'Iron will, iron blood!', 'Iron will, iron blood!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78528, 'enUS', 'The Iron Tide will consume you!', 'The Iron Tide will consume you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78529, 'enUS', 'The slavers\' guild is only too happy to meet your needs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78530, 'enUS', 'Intruders!', 'Intruders!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78531, 'enUS', 'I couldn\'t agree more. Just let me know when you\'d like to come see our wares.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78532, 'enUS', 'No need for ire. You and I are friends. We respect the might of the Iron Horde.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78533, 'enUS', 'I lapped a clefthoof the other day. Hrah ha ha.', 'I lapped a clefthoof the other day. Hrah ha ha.', 26124);
