INSERT INTO `broadcast_text_locale` VALUES (78540, 'enUS', '', 'Thank you brave heroes.  Without your help Amnennar would have returned to Azeroth and brought untold horrors with him.$b$bI will return this dangerous artifact to my brothers and sisters of the red dragonflight.  Perhaps we can find a way to destroy it.$b$bI am in your debt.', 26124);
