INSERT INTO `broadcast_text_locale` VALUES (78587, 'enUS', 'You be lookin\' for de bank mon?  You be followin\' the road to de north all de way to Orgrimmar.  Speak to de guards there and they can help ya.', 'You be lookin\' for de bank mon?  You be followin\' the road to de north all de way to Orgrimmar.  Speak to de guards there and they can help ya.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78588, 'enUS', 'Ya be a long way off mon.  De bank be in Orgrmmar.  Follow de road to de north, ya can\'t miss de place.', 'Ya be a long way off mon.  De bank be in Orgrmmar.  Follow de road to de north, ya can\'t miss de place.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78589, 'enUS', 'Lookin\' for a ride eh?  Burok be at the southern end of town, next to de wind riders.', 'Lookin\' for a ride eh?  Burok be at the southern end of town, next to de wind riders.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78590, 'enUS', 'De inn be just north of de road leading to de Barrens.  Grosk be helpin\' ya out.', 'De inn be just north of de road leading to de Barrens.  Grosk be helpin\' ya out.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78591, 'enUS', 'Havin\' some furry friends needin\' a place to stay? Shoja\'my, outside of de inn, can help.', 'Havin\' some furry friends needin\' a place to stay? Shoja\'my, outside of de inn, can help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78592, 'enUS', 'You be needin\' to see a trainer, mon?', 'You be needin\' to see a trainer, mon?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78593, 'enUS', 'Jabul, can help you.  He\'s been givin\' new druids trainin\' for years now.', 'Jabul, can help you.  He\'s been givin\' new druids trainin\' for years now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78594, 'enUS', 'The Hunter Trainer\'s name is Thotar.  Seek him out inside the burrow on the southwest corner of Razor Hill.', 'The Hunter Trainer\'s name is Thotar.  Seek him out inside the burrow on the southwest corner of Razor Hill.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78595, 'enUS', 'Ah, ya be after de mojo, eh?  Un\'Thuwa can teach you.  Ya be findin\' him near the entrance to the barracks.', 'Ah, ya be after de mojo, eh?  Un\'Thuwa can teach you.  Ya be findin\' him near the entrance to the barracks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78596, 'enUS', 'Ya be lookin\' for Tai\'jin then. She be spendin\' most of her time in de barracks.', 'Ya be lookin\' for Tai\'jin then. She be spendin\' most of her time in de barracks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78597, 'enUS', 'Kaplak be da orc you be lookin\' for mon.  He be in de burrow south of da inn.', 'Kaplak be da orc you be lookin\' for mon.  He be in de burrow south of da inn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78598, 'enUS', 'Swart be communin\' wit de elements inside of da barracks.  It be de large building in de center of town.', 'Swart be communin\' wit de elements inside of da barracks.  It be de large building in de center of town.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78599, 'enUS', 'Lookin\' for Dhugru Gorelust eh?  He be practicin\' his craft behind de barracks.', 'Lookin\' for Dhugru Gorelust eh?  He be practicin\' his craft behind de barracks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78600, 'enUS', 'You be messin\' with some bad mojo mon, but Dhugru be behind de barracks.', 'You be messin\' with some bad mojo mon, but Dhugru be behind de barracks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78601, 'enUS', 'De warrior trainer, Tarshaw Jaggedscar, be inside de barracks on de east side of town.', 'De warrior trainer, Tarshaw Jaggedscar, be inside de barracks on de east side of town.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78602, 'enUS', 'Runda be de one person you be needin\' to talk to.\n\nShe be just north of de barracks entrance.', 'Runda be de one person you be needin\' to talk to.\n\nShe be just north of de barracks entrance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78603, 'enUS', 'Hammer and tongs, beat to this song...s, stoke the fire higher.', '', 26124);
