INSERT INTO `broadcast_text_locale` VALUES (78613, 'enUS', 'The nearest bank is in Orgrimmar.  Follow the road north and you cannot miss the big gates leading to the city.', 'The nearest bank is in Orgrimmar.  Follow the road north and you cannot miss the big gates leading to the city.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78614, 'enUS', '', 'We did it! Hopefully we can clean the water quickly so we can regrow the crops!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78615, 'enUS', 'Burok can be found near the wind riders on the southern end of town.  He can take you where you need to go.', 'Burok can be found near the wind riders on the southern end of town.  He can take you where you need to go.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78616, 'enUS', 'Grosk and his inn can be found in the northwest corner of Razor Hill.', 'Grosk and his inn can be found in the northwest corner of Razor Hill.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78617, 'enUS', 'Shoja\'my is the one you are looking for.  He is just outside of the inn.', 'Shoja\'my is the one you are looking for.  He is just outside of the inn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78618, 'enUS', 'Which trainer do you seek?', 'Which trainer do you seek?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78619, 'enUS', 'Jabul has trained many, perhaps he can help you too.', 'Jabul has trained many, perhaps he can help you too.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78620, 'enUS', 'You seek bigger game?  Perhaps Thotar can teach you what you need to know.  He can be found inside the burrow on the southwest corner of Razor Hill.', 'You seek bigger game?  Perhaps Thotar can teach you what you need to know.  He can be found inside the burrow on the southwest corner of Razor Hill.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78621, 'enUS', 'You seek knowledge of the powers of magic.  There are many things Un\'Thuwa can teach you.  Find him just north of the entrance to Razor Hill\'s barracks.', 'You seek knowledge of the powers of magic.  There are many things Un\'Thuwa can teach you.  Find him just north of the entrance to Razor Hill\'s barracks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78622, 'enUS', 'Tai\'jin can be found inside the barracks, the largest building in town.', 'Tai\'jin can be found inside the barracks, the largest building in town.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78623, 'enUS', 'You seek Kaplak.  He can be found in the burrow in the southwest area of Razor Hill.  Do not take your eyes off of that one for a second.', 'You seek Kaplak.  He can be found in the burrow in the southwest area of Razor Hill.  Do not take your eyes off of that one for a second.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78624, 'enUS', 'Swart is inside the barracks, on the east side of Razor Hill.', 'Swart is inside the barracks, on the east side of Razor Hill.', 26124);
