INSERT INTO `broadcast_text_locale` VALUES (78627, 'enUS', 'Earth Mother watch over you traveler, what is it you seek?', 'Earth Mother watch over you traveler, what is it you seek?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (78628, 'enUS', 'Look for Runda.\n\nShe\'s usually just north of the barracks entrance.', 'Look for Runda.\n\nShe\'s usually just north of the barracks entrance.', 26124);
