INSERT INTO `broadcast_text_locale` VALUES (79234, 'enUS', 'Korhol, Zhakta, Trokar... move in from all sides. Gargrak, hold for now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79235, 'enUS', 'ZHAKTA NO!... you will be avenged!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79236, 'enUS', 'Bring me that $r $c\'s head!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79237, 'enUS', 'Dammit all. Gargrak, go now... kill the blind shaman.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79238, 'enUS', 'Call in all reinforcements. I want the She-Wolf taken alive.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79239, 'enUS', 'RETREAT! WE\'LL BE BACK, FROSTWOLVES!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79240, 'enUS', 'See what you can dig up. If the Shadowmoon clan joins forces with the Iron Horde, I worry our darkest days are yet to come.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79241, 'enUS', 'Army! Grow!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79242, 'enUS', 'No chance now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79243, 'enUS', 'Little ones! Grow! Kill!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79244, 'enUS', 'Eat you friends! Then eat you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79245, 'enUS', 'It appears the Shadowmoon clan is having difficulty integrating with the Iron Horde. This could play out in our favor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79246, 'enUS', 'You ours now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79247, 'enUS', 'Healing.', 'Healing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79248, 'enUS', 'I\'ve given you some smoke grenades. Use these to help Thaelin target the Arkonite Protector.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79249, 'enUS', 'I\'ve got enough blasting powder to wipe that gronn right off the face of Draenor. Just show me where to drop it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79250, 'enUS', 'The spirits are restless, Durotan.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79251, 'enUS', '', 'This.. this is horrible. What are we going to do?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79252, 'enUS', '', 'I ran as soon as I had a chance. Poor Laabru. I hope he is still alive.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79253, 'enUS', 'We have to break free of these bonds. Hurry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79254, 'enUS', 'We kill you soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79255, 'enUS', 'This Iron Tide comes for us because Durotan is strong and the Frostwolf would not join them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79256, 'enUS', 'Wake up! We need to break free now! Look at that thing!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79257, 'enUS', 'We need backup!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79258, 'enUS', 'Help! We have intruders!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79259, 'enUS', 'Iron Horde, we have unwanted visitors!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79260, 'enUS', 'Did you hear that?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79261, 'enUS', 'I am on the way!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79262, 'enUS', 'The Iron Horde will crush you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79263, 'enUS', 'What was that noise?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79264, 'enUS', 'It is time for death!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79265, 'enUS', 'To arms!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79266, 'enUS', '', 'I hear the sounds of battle!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79267, 'enUS', '', 'Hold on, we are on the way!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79268, 'enUS', '', 'Blood and conquest!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79269, 'enUS', 'We are needed!', 'We are needed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79270, 'enUS', 'Destroy the intruders!', 'Destroy the intruders!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79271, 'enUS', 'I hear a battle! Move it!', 'I hear a battle! Move it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79272, 'enUS', 'Damn the Iron Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79273, 'enUS', 'Healing Loop.', 'Healing Loop.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79278, 'enUS', 'Arise my creation, slay these intruders!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79279, 'enUS', 'Many Frostwolf have fallen here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79280, 'enUS', 'They\'re using the souls of my people to raise the Colossal as a weapon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79281, 'enUS', '$n, destroy these Soul Shaman and stop the ritual.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79282, 'enUS', 'My warriors and I will rescue our brethren that still live.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79283, 'enUS', '', 'Where\'s my daddy?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79284, 'enUS', 'You cannot defeat us all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79285, 'enUS', 'For the Iron Wolf!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79286, 'enUS', '', 'DADDY!!!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79287, 'enUS', 'Thunderlords, to arms!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79288, 'enUS', 'Frostwolf scum!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79289, 'enUS', 'You are no match for the Thunderlords!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79290, 'enUS', '<The gyro looks like it\'s held together with duct tape and bubble gum. Thaelin seems to know what he is doing though. It should be safe.>', '<The gyro looks like it\'s held together with duct tape and bubble gum. Thaelin seems to know what he is doing though. It should be safe.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79291, 'enUS', 'Return to the Observatory?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79292, 'enUS', 'I\'ll make a trophy from your head.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79293, 'enUS', 'Return to the Observatory?', 'Return to the Observatory?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79294, 'enUS', 'Lok\'tar Ogar, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79295, 'enUS', 'Wander, infirmary.', 'Wander, infirmary.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79296, 'enUS', 'Did you get separated from your precious pack?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79297, 'enUS', 'Thank you, $n. With those brutes dead it will be easier to convince the others to rise up.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79298, 'enUS', 'The Iron Horde will surely think twice before they hold a Draenei captive again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79299, 'enUS', 'It is good to see you again, $n. Without you and Thaelin I would surely be doomed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79300, 'enUS', 'The tip of the Thunderlord spear, no greater honor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79301, 'enUS', 'Me and Thunderhoof are going to crush all of you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79302, 'enUS', '', 'Death to the puny Frostwolf clan.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79303, 'enUS', '', 'Here, this herb\'s effects will put a spring in your step. Be careful, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79304, 'enUS', '', 'Let me know when you\'re ready!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79305, 'enUS', 'Hurry $p, Ga\'nar is beneath us in the lair of Grulloc. You should join him before he kills everyone without you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79306, 'enUS', 'Anvil.', 'Anvil.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79307, 'enUS', 'Anvil done.', 'Anvil done.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79308, 'enUS', 'The Shamanstone hums in the presence of powerful heroes.', 'The Shamanstone hums in the presence of powerful heroes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79309, 'enUS', 'Blessing of the Wolf', 'Blessing of the Wolf', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79310, 'enUS', 'Spirit of the Wolf', 'Spirit of the Wolf', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79315, 'enUS', '*Cough, Sputter*', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79316, 'enUS', 'Did you see THIS coming, you blind fool? Hah! Hah!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79317, 'enUS', 'Kalandrios, I would like to witness the Ritual of Binding again.', 'Kalandrios, I would like to witness the Ritual of Binding again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79318, 'enUS', 'Get out of my face, scum.$B$BWhen I break free, I\'m going to cut you in two.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79319, 'enUS', '', 'Sorcerous coward.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79320, 'enUS', '', 'Reinforcements to the east.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79321, 'enUS', '', 'Watch for Thunderlord from the ledge to the north.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79322, 'enUS', '', 'To the south, now my brothers and sisters.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79323, 'enUS', '', 'They can only attack us when most of our clan is away. Kill these Thunderlord cowards.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79324, 'enUS', '', 'Watch for their warleader, Gargrak.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79325, 'enUS', '', 'We will make Durotan proud this day!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79326, 'enUS', '', 'Come down and face me, \"Iron Wolf\". I\'ll show you how a true wolf fights!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79327, 'enUS', '', 'Protect the Pool of Visions. Do not let them get to the Farseer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79328, 'enUS', 'Check Crate.', 'Check Crate.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79329, 'enUS', 'Lok\'tar Ogar!', 'Lok\'tar Ogar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79330, 'enUS', 'Despicable. The Iron Wolf has no respect for beast or brother - they are all just weapons for the hunt.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79331, 'enUS', 'Never enough...', 'Never enough...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79332, 'enUS', 'I need to look this up.', 'I need to look this up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79333, 'enUS', 'We\'ve called every able-bodied soldier to defend the city.$B$BStay stalwart, hero. This is the hour for courage.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79334, 'enUS', 'Reporting for duty.', 'Reporting for duty.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79335, 'enUS', 'The Iron Horde are moving toward the Temple of Karabor. The attack has beg--', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79336, 'enUS', 'Wait... what is that light?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79337, 'enUS', 'It\'s too late for this one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79338, 'enUS', '', 'How are you feeling, Hemma?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79339, 'enUS', 'Yes, Princess.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79340, 'enUS', '', 'Just a sip, be easy on her!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79341, 'enUS', '', 'Thank you, Kadus. We have to try. There\'s too much at stake.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79342, 'enUS', 'I\'m ready whenever you are.', 'I\'m ready whenever you are.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79343, 'enUS', 'Docking.', 'Docking.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79344, 'enUS', '', 'I refuse to remain in Darnassus any longer. There are people that need help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79345, 'enUS', 'Hydrating.', 'Hydrating.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79346, 'enUS', '', 'Here, $p. Give this potion to Hemma. It might help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79347, 'enUS', 'Right away, Princess.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79348, 'enUS', 'Hemma. Drink this. It\'ll help you feel better.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79349, 'enUS', 'No effect, Princess. We tried.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79350, 'enUS', 'You are fierce, pup, but your attachments make you weak!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79351, 'enUS', 'Free yourself of weakness, or be swept away by the Iron Tide!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79352, 'enUS', '', 'I... I feel...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79353, 'enUS', '', 'Asha?! Asha, NO!!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79354, 'enUS', '', 'HnnGYAAHH!!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79355, 'enUS', '', 'HUNGRY!!!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79356, 'enUS', '', 'Hemma! Please, wait! Control yourself!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79357, 'enUS', '', '$p, you\'re the fastest one here. Track Hemma down and try to get her under control. We\'ll catch up!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79358, 'enUS', '', 'I\'m losing control... please, help...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79359, 'enUS', 'Here, drink this. How are you feeling?', 'Here, drink this. How are you feeling?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79360, 'enUS', 'This talbuk has been ripped to pieces by Hemma. She must be close by.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79361, 'enUS', 'Ah, that\'s the stuff...', 'Ah, that\'s the stuff...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79362, 'enUS', '', 'Coward! Release Karg and face me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79363, 'enUS', '', 'Wh - where am I? Where is the spirit?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79364, 'enUS', 'You lost control. You\'re on a hilltop near camp.', 'You lost control. You\'re on a hilltop near camp.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79365, 'enUS', '', 'Hemma, we\'re here. Are you alright?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79366, 'enUS', 'Go, $c. Wreak havoc. Impose justice on those who so thoroughly deserve it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79367, 'enUS', '', 'I don\'t remember much. Just a spirit... of pure rage. It called to me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79368, 'enUS', '', 'I... I don\'t feel well.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79369, 'enUS', 'U\'luut thanks you for saving him and the forests of Shadowmoon Valley.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79370, 'enUS', 'Hi, you!', 'Hi, you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79371, 'enUS', 'Hi. See you later!', 'Hi. See you later!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79372, 'enUS', '', 'Thank you. I hope Princess Greymane can find the cure soon. It has be out here somewhere.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79373, 'enUS', 'Lok\'tar, $p!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79374, 'enUS', 'Lok\'tar!', 'Lok\'tar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79375, 'enUS', 'Did you think me defeated so easily?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79376, 'enUS', 'Who dares disturb my research!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79377, 'enUS', 'Stand back!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79378, 'enUS', 'We have driven the Iron Horde back, $p, but the inner chamber remains sealed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79379, 'enUS', 'It seems the elements of Draenor do not respond to me as they do on Azeroth...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79380, 'enUS', 'Go on ahead. Durotan awaits. I must catch my breath.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79381, 'enUS', 'I\'m crazy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79382, 'enUS', '', 'Stay away from me! Stay away! I\'ll kill you! I\'ll kill you all!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79383, 'enUS', 'I can bring it down but I need time to harness the elements.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79384, 'enUS', '', 'The might of my ancestors flows through me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79385, 'enUS', 'Kilrogg! I should have known you were behind this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79386, 'enUS', 'Heh... that mutt... <cough> is being broken... with the other beasts.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79387, 'enUS', 'You were fools to come here. I will rip the souls from your bodies and raise the Colossal to crush your feeble clan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79388, 'enUS', 'To Bladespire! Attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79389, 'enUS', 'There is no time to waste! We strike NOW!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79390, 'enUS', 'We must breach the inner chamber. Kilrogg cannot get away with this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79391, 'enUS', '$p, defend me while I destroy the barrier!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79392, 'enUS', 'This one\'s lost blood.', 'This one\'s lost blood.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79393, 'enUS', '', 'I... I can\'t believe he\'s gone...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79394, 'enUS', 'Yrel! The defense crystal is broken. The Iron Horde have overrun Karabor. We\'d need a miracle to break through.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79395, 'enUS', '', '%s wipes away a tear.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79396, 'enUS', '', 'Velen\'s sacrifice means nothing if the temple falls.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79397, 'enUS', 'Dorogg has fallen! Stay close, Frostwolves - the citadel is not yet ours.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79398, 'enUS', '<Dulcet chiming tones reverberate within your mind.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79399, 'enUS', 'I am ready to join the attack against the Iron Horde.', 'I am ready to join the attack against the Iron Horde.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79400, 'enUS', 'Press the attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79401, 'enUS', 'The Frostwolves will triumph.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79402, 'enUS', 'This fortress will fall.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79403, 'enUS', 'Do not give up!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79404, 'enUS', 'Gather your strength!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79405, 'enUS', '*Sniff*', '*Sniff*', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79406, 'enUS', 'Carry the light, brothers and sisters!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79407, 'enUS', 'Curse him!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79408, 'enUS', 'That was barely a scratch! I should recover quickly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79409, 'enUS', '', 'Samaara is the only family that I have. As long as I can remember, she has taken care of me.$B$BShe is good and kind, whereas I am... not as convicted.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79410, 'enUS', '', 'For many years, the Shadowmoon Clan and the draenei of Karabor have lived in relative peace.$B$BThings began to change after this... Iron Horde... arrived.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79411, 'enUS', '', 'Maraad says his injury is not serious, but in truth, he was gravely wounded by Ner\'zhul. Though I am not sure which suffered greater ill - his body or his pride.$B$BNevertheless, he was very lucky you showed up when you did.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79412, 'enUS', 'Can you believe this?', 'Can you believe this?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79413, 'enUS', 'What do we have today?', 'What do we have today?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79414, 'enUS', 'Who are you?', 'Who are you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79415, 'enUS', 'WEEE wee wee!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79416, 'enUS', '', 'I will coil your souls into darkness!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79417, 'enUS', 'Your blissful afterlife is over.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79418, 'enUS', '', 'Too late... my Master... comes...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79419, 'enUS', '', 'Shadows envelop you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79420, 'enUS', '', 'Your oath is unfinished!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79421, 'enUS', '', 'Return to this world!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79422, 'enUS', '', 'The spirits are mine to command!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79423, 'enUS', '', 'I will drown this world in shadows!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79428, 'enUS', 'Your undying souls will fuel my ascension from the darkness!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79429, 'enUS', 'Cower before me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79431, 'enUS', '', 'I\'m free!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79432, 'enUS', '', 'Thank you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79433, 'enUS', '', 'My torment is over!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79434, 'enUS', 'Keep your wits about you. Some of the refugees reported seeing a plant hydra in the forest near the podling den to the west.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79435, 'enUS', 'It is up to you, champion. I will stay here and help with the wounded slaves you rescued.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79436, 'enUS', 'Please bring her back, $n. She\'s the only family I have left.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79437, 'enUS', '', 'Nothing worthwhile ever is.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79438, 'enUS', 'We were hunting a large pale orc when the spiders attacked.$b$bI lost my assistant Ganky in the chaos. I was bitten countless times as I ran.$b$bThe spider bites are affecting my ability to think straight. I need to create a cure quickly before my mind escapes me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79439, 'enUS', 'It looks like this is the bowl Helga was going to use to create a cure.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79440, 'enUS', 'Mushroom show future.  Believe!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79441, 'enUS', 'Wash in holy spores.  Feel might of mushroom!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79442, 'enUS', 'Visions this mushroom has given.  Truth told!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79443, 'enUS', 'Drek\'Thar, old friend, you\'re alive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79444, 'enUS', 'Who? These strangers you\'ve brought?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79445, 'enUS', 'I know your eyes don\'t work, shaman, but this one has green skin.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79446, 'enUS', 'Hmph. Yes. Ga\'nar is already causing trouble.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79447, 'enUS', 'Very well, strangers. You have earned the trust of our shaman. I will not turn away the help.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79448, 'enUS', 'I can barely stand. But for the actions of these heroes, I would be dead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79449, 'enUS', 'These are champions from a faraway realm, pledged to fight the Iron Horde.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79450, 'enUS', 'I do not need eyes to see it; they are noble of heart. They saved our kin -- including your brother.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79451, 'enUS', 'Come, friends. If you wish to fight with our clan, we welcome you to receive our blessings.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79452, 'enUS', 'Reach out to the Shamanstone. Embrace the wisdom of this land and the ancestors that came before us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79453, 'enUS', 'I am still scarred by those infernal warlocks. The spirits are quiet.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79454, 'enUS', 'I can feel it. This land speaks to me, but in a language I do not yet understand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79455, 'enUS', 'The spirits will never abandon you, Drek\'Thar. This I can promise.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79456, 'enUS', '', 'Come with me, shaman. Let me see your wounds.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79457, 'enUS', '', 'Draka looks at Drek\'Thar, concerned.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79458, 'enUS', 'Warriors! Today the tide turns.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79459, 'enUS', 'We will claim Bladespire Citadel as our own.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79460, 'enUS', 'Fight as one. We win this - together!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79461, 'enUS', 'Ga\'nar is a great fighter, but he is impatient. He has taken some of our best warriors with him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79462, 'enUS', 'He never listens. His ruthlessness may cost us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79463, 'enUS', 'Here - Gormaul Tower. This will be our foothold. Leave none alive! LOK\'TAR OGAR!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79464, 'enUS', 'Durotan, your brother has already rushed ahead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79465, 'enUS', 'The Bladespire ogres threw me into that hellhole. I\'ll notch my axe with their skulls until vengeance is mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79466, 'enUS', 'Bladespire Citadel! I will have my vengeance on those ogres, even if I have to tear it down stone by stone.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79467, 'enUS', '', 'Keep the Iron Horde at bay!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79468, 'enUS', 'My friends, you have earned a hero\'s welcome. Follow us to the Frostwolf Chieftain.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79469, 'enUS', 'Wise-ear! You are alive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79470, 'enUS', 'My eyes may not work, but the familiar chill of the air is comforting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79471, 'enUS', 'I worry about Ga\'nar. Always quick to fight. His need for vengeance will make him more reckless than ever.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79472, 'enUS', 'Ah yes. Garad\'s youngest: a noble soul named Durotan.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79473, 'enUS', 'Wise beyond his years, but still young. I fear he has been thrust into the Gronn\'s jaws - our people are in great danger.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79474, 'enUS', 'Here we are! Wor\'gol, home of the Frostwolves.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79475, 'enUS', 'You mentioned the Frostwolf Chieftain...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79476, 'enUS', 'Durotan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79477, 'enUS', 'Visions! No!  You pay for interfering!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79478, 'enUS', 'You fight well, $p! With you here, the odds are stacked in our favor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79479, 'enUS', 'Lokra... with you at my side, I feel invincible! Let us challenge the Iron Wolf right now and end this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79480, 'enUS', '', 'We leave now, and return to the pack. The Iron Wolf will answer to the full might of the Frostwolf clan.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79481, 'enUS', 'Lead us to Durotan, old friend!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79482, 'enUS', '', 'Fomic ore is tougher than hardened steel. You will need my goren-toothed pick axe to cut through the rock.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79483, 'enUS', '', 'The spirits were kind to guide you to me at this moment.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79484, 'enUS', '', 'It is done! The blade is forged.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79486, 'enUS', '|TInterface\\Icons\\mocking_banner:20|t%s places a |cFFFF0000|Hspell:153799|h[Rallying Banner]|h|r to call nearby allies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79487, 'enUS', '', 'You now stand before Na\'Shra! Weaponsmith of the Frostwolf Clan. For your service, I give you mine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79488, 'enUS', '', 'Oops! Wrong wire. Watch out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79491, 'enUS', '', 'You have done me a great service. This blade is as much yours as it is mine.$b$bI offer you this blood oath: I shall serve your cause and lend my knowledge of blades and mining to your forces.$b$BWill you accept it?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79492, 'enUS', 'Accept the Blood Oath of Blademaster Na\'Shra. (Garrison Follower)', 'Accept the  Blood Oath of Blademaster Na\'Shra. (Garrison Follower)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79495, 'enUS', 'You are hurt. Your flesh is pale.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79496, 'enUS', 'Bandage up those who can still walk! No orc of able body will be denied this fight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79497, 'enUS', 'So hungry.', 'So Hungry.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79498, 'enUS', 'Mixing', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79499, 'enUS', 'Reading', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79500, 'enUS', 'The battle is joined. Lok-Narash!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79501, 'enUS', 'Reinforcements approach. Attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79502, 'enUS', 'The stench of a boar beast. Beware its tusks... Charge!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79503, 'enUS', 'The tower is ours! Regroup and press on!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79504, 'enUS', 'I will ride ahead. Frostwolves, hold the tower. Igrimar, with me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79505, 'enUS', 'I will forestall any counterattacks by the enemy. Lok\'tar ogar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79506, 'enUS', '$p, I need you out there. There\'s no way we\'ll be able to fight them off without you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79507, 'enUS', 'Disrupt Rune Conduits to weaken Gor\'ashan\'s defenses.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79508, 'enUS', 'Helga has been like this ever since she returned to camp.$b$bShe was babbling something about needing a cure -- then she started eating dirt.$b$bI am not getting paid enough for this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79509, 'enUS', 'Why can\'t I ever catch a break?$b$bI figured tagging along with a Nesingwary was a surefire way to make some moolah.$b$bI should have stayed in Azeroth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79510, 'enUS', '', 'What about me? $p and I are a team!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79511, 'enUS', 'Absolutely not. I\'m not going to lose you again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79512, 'enUS', '', 'You\'re going to be ok... I promise... just hang in there...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79513, 'enUS', 'The barrier is down! Let us put an end to this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79514, 'enUS', 'Invite Helga to join the caravan?', 'Invite Helga to join the caravan?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79515, 'enUS', '', 'The Pale walk these lands at night. They have no morals. They attack children and the elderly without even a second thought.$b$bI will not let their horrible deeds go unpunished.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79516, 'enUS', '', '$GHe:She;\'s coming to! $n, talk to me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79517, 'enUS', '', 'Am I alive? Who am I? Who are we? So much pain!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79518, 'enUS', '', 'So many spiders... So many bunnies...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79519, 'enUS', '|TInterface\\Icons\\spell_shadow_chilltouch.blp:20|t%s casts |cFFFF0000|Hspell:154766|h[Summon Ancient Spirits]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79520, 'enUS', 'Helga was writing in her journal when she lost it. It\'s right over here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79521, 'enUS', 'Invisible bunnies? Sheesh... talk about crazy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79522, 'enUS', 'I would have read it myself, but it\'s not in my job description.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79523, 'enUS', 'You have to do what with the eyeballs? That\'s just gross.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79524, 'enUS', 'Darksting Queen huh? Good luck with that. I\'ll be back here taking care of Helga if you need me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79525, 'enUS', '', 'I... I feel much better. Thank you, $c. Now listen carefully. We need your help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79526, 'enUS', 'I hope this works. I\'m living paycheck to paycheck over here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79529, 'enUS', '', 'Be careful, $n. I\'ll join you as soon as I can.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79530, 'enUS', 'Not for long.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79531, 'enUS', '\"Iron Wolf\" indeed! A coward, hiding behind his minions.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79532, 'enUS', '', 'That\'s the third assistant I\'ve lost this week.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79533, 'enUS', 'Stand down, Warlord. It is over.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79534, 'enUS', '', 'I can\'t thank you enough, $c. This could have been a real disaster.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79535, 'enUS', 'Could have been a disaster? I gotta find a new line of work.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79536, 'enUS', '...Fenris?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79537, 'enUS', 'Helga has no respect for me or any of her other assistants for that matter.$b$bAnd honsetly... the pay ain\'t all that hot.$b$bI think it\'s time I find a new employer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79538, 'enUS', 'Fenris!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79539, 'enUS', 'Invite Greblin to join the caravan?', 'Invite Greblin to join the caravan?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79540, 'enUS', 'This deal is getting worse all the time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79541, 'enUS', 'Sure I\'ll join you. As long as the money is good and you promise I won\'t get eaten by spiders.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79542, 'enUS', '', 'Great... now I\'ve lost four assistants this week. Thanks, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79543, 'enUS', '', 'I would be happy to join you and the caravan, $n. Start packing our stuff Greblin.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79544, 'enUS', '', '*Sigh* It\'s so hard to find good help these days', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79545, 'enUS', 'Pack it yourself. I quit. I\'m going to find a job that doesn\'t involve being eaten by spiders.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79546, 'enUS', 'Thought we were dead, eh? LAUGHING SKULL! LAUGHING SKULL! HAHAHAHAHA!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79547, 'enUS', 'Laughing Skull scum!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79548, 'enUS', 'What? No more fancy toys to hide behind? Getting a little scared are you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79549, 'enUS', 'I\'m gonna cut a smile into that pretty orc face of yours!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79550, 'enUS', 'We are going to rip your little Iron Horde apart piece by piece!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79551, 'enUS', 'My family! My friends! My home! You burned it all and I want it back!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79552, 'enUS', 'And that is the first name on my list. So many more names to go.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79553, 'enUS', 'Chilltongue - devour them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79554, 'enUS', 'It\'s cold out here.', 'It\'s cold out here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79555, 'enUS', 'Sarge hears that, you\'ll be on ridge duty all winter.', 'Sarge hears that, you\'ll be on ridge duty all winter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79557, 'enUS', 'Here you go. I borrowed the wrench from Romuul to fix my spyglass. Tell him I say thank you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79558, 'enUS', '', 'I foresaw your arrival, $n, so here is the crystal that Romuul needs.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79559, 'enUS', 'I don\'t drink anymore, $Glad:lass;. Swore ne\'er ta down a cold one until tha day I can see me sweet Elsa once more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79560, 'enUS', 'Me on tha other hand, I brought plenty o\' stout ta share! Give this ta our wiggly-haired blue friend downstairs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79561, 'enUS', 'What are you doing?', 'What are you doing?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79562, 'enUS', '', 'We are too few to stop the Iron Tide dead in its tracks.$B$BBut, we can fight smartly and take full advantage of their mistakes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79563, 'enUS', '%s begins to |cFFFF0404|Hspell:153804|h[Inhale]|h|r his enemies!', '%s begins to |cFFFF0404|Hspell:153804|h[Inhale]|h|r his enemies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79564, 'enUS', '', 'Ah, right here. This disc should contain the information that Romuul is looking for.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79565, 'enUS', '%s\'s piercing screech attracts nearby Carrion Worms!', '%s\'s piercing screech attracts nearby Carrion Worms!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79566, 'enUS', 'Brother, wait. Let us talk. We are still family.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79567, 'enUS', 'Champion - focus on Fenris.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79568, 'enUS', 'We\'ll take care of Chilltongue!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79569, 'enUS', '$p, don\'t let Fenris escape!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79570, 'enUS', 'Brother, wait. Let us talk. We are still family.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79571, 'enUS', '\"Family.\" You are soft - like our father!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79572, 'enUS', 'You dare speak of our father! You gloated over his mangled corpse!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79573, 'enUS', 'A small chieftain of an insignificant clan. I saw what father didn\'t - that we could be so much more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79574, 'enUS', 'Reinforcements! Slay the Frostwolf pups!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79575, 'enUS', 'Ga\'nar, cover the rear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79576, 'enUS', 'Yes, brother!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79577, 'enUS', 'Stinking animals, all of you! Sleeping with your dogs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79578, 'enUS', 'Orcs are meant to be masters of beasts! Masters of all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79579, 'enUS', 'No slave fights with the will of one who is free.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79580, 'enUS', 'Finish this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79581, 'enUS', 'Careful, brother!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79582, 'enUS', 'Animals... Your clan is finished... The Iron Horde already comes...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79583, 'enUS', 'Let them come! Traitor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79584, 'enUS', 'The Thunderlords retreat! They are making their way through Thunder Pass to the Iron Horde.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79585, 'enUS', 'They are going to retaliate. We must be ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79586, 'enUS', 'How could he have done this to us?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79587, 'enUS', 'Those were my own nephews I slaughtered in battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79588, 'enUS', 'What is this...?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79589, 'enUS', 'The \"Iron Wolf\" is our lost brother. Was... our brother. He was family.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79590, 'enUS', 'No longer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79591, 'enUS', 'We will bury him as a Thunderlord. With the honors due a chieftain. It was his choice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79592, 'enUS', 'Come, Durotan. If the Iron Horde wants a fight, we will give it to them. They will NEVER take the pass!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79593, 'enUS', 'Ga\'nar... it is an honor to fight with you at my side. Brother.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79594, 'enUS', '', 'Are you okay, husband?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79595, 'enUS', 'No, I will never be okay with this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79596, 'enUS', 'Come! To Thunder Pass. We are outnumbered 50 to 1. They don\'t stand a chance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79597, 'enUS', 'Thank you, child. I can always count on you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79598, 'enUS', 'There\'s nothing here.', 'There\'s nothing here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79599, 'enUS', 'I will speak with this slaver and see if he knows where we might find Orgrim.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79600, 'enUS', 'In the meantime, help Drek\'Thar find some method of destroying that cannon before they find a way to move it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79601, 'enUS', 'Bring me that ogre, $p. He will be the key to finding Orgrim in this wasteland.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79602, 'enUS', 'Lay down your axe and warm your belly with a brew by the fire.', 'Lay down your axe and warm your belly with a brew by the fire.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79603, 'enUS', 'There! Is this what you seek, Prophet?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79604, 'enUS', 'Grow! Grow!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79605, 'enUS', 'Power in mushrooms.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79606, 'enUS', 'More! More!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79607, 'enUS', 'We draenei are not originally from this world. Our homeworld was named Argus, but it was taken over by the Burning Legion.', 'We draenei are not originally from this world. Our homeworld was named Argus, but it was taken over by the Burning Legion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79608, 'enUS', 'A small group of survivors, led by Prophet Velen, were able to escape and flee.', 'A small group of survivors, led by Prophet Velen, were able to escape and flee.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79609, 'enUS', '', 'Kron approaches! Champion, with me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79610, 'enUS', 'Once, our people had three leaders. Their names were Velen, Archimonde, and Kil\'jaeden.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79611, 'enUS', 'When the Burning Legion came to Argus, Velen was the only one who resisted their promise of immortality and power.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79612, 'enUS', 'Kil\'jaeden leads the Burning Legion\'s demonic army. Once, he was a wise leader of our people, but he fell to the dark promises of the enemy.', 'Kil\'jaeden leads the Burning Legion\'s demonic army. Once, he was a wise leader of our people, but he fell to the dark promises of the enemy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79613, 'enUS', 'He would chase us to the end of the universe to see our people destroyed.', 'He would chase us to the end of the universe to see our people destroyed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79614, 'enUS', 'After our ancestors fled from Argus, they traveled across the stars seeking refuge from the Burning Legion.', 'After our ancestors fled from Argus, they traveled across the stars seeking refuge from the Burning Legion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79615, 'enUS', 'One day, the ships\' energy source, powered by the brilliant light of the naaru, died out. They jettisoned the source from the ship, but it was too late.', 'One day, the ships\' energy source, powered by the brilliant light of the naaru, died out. They jettisoned the source from the ship, but it was too late.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79616, 'enUS', 'Naaru are wise and powerful beings of pure Light. It was a naaru who came to Prophet Velen and helped engineer our escape from the Burning Legion.', 'Naaru are wise and powerful beings of pure Light. It was a naaru who came to Prophet Velen and helped engineer our escape from the Burning Legion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79617, 'enUS', 'Much about them is still unknown, but when a naaru\'s energy is depleted, it inverts into a darkened state.', 'Much about them is still unknown, but when a naaru\'s energy is depleted, it inverts into a darkened state.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79618, 'enUS', 'Dark naaru are beings of pure shadow, and fearsome to behold.', 'Dark naaru are beings of pure shadow, and fearsome to behold.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79619, 'enUS', 'If I try to release Exurotus it is sure to bring Kron down on us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79620, 'enUS', 'As pieces of the ship exploded around them, they navigated toward the closest world - a tranquil, untamed land we would later call Draenor.', 'As pieces of the ship exploded around them, they navigated toward the closest world - a tranquil, untamed land we would later call Draenor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79621, 'enUS', 'We have company, Master.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79622, 'enUS', 'Tagar, deal with the intruder.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79623, 'enUS', 'Champion! I heard Drek\'Thar\'s call. I am with you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79624, 'enUS', 'Frostwolves! The Iron Horde is coming. As we speak, they march on Thunder Pass.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79625, 'enUS', 'But the Frostwolf clan is reunited! Together, we can protect this land!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79626, 'enUS', 'Lok-Narash, Frostwolves! Prepare for battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79627, 'enUS', 'You know me, brother. I\'m always ready to fight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79628, 'enUS', 'We will hold the line here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79629, 'enUS', '', 'Drek\'Thar, you can speak with the earth. Do you think you can collapse this canyon?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79630, 'enUS', 'Stop them! They can\'t be allowed to disturb the master from his trance!', 'Stop them! They can\'t be allowed to disturb the master from his trance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79631, 'enUS', 'It would be a momentous task... I cannot make any promises.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79632, 'enUS', 'I can. Shaman, if you bring that cliff wall down, I promise that our enemy will be waiting right underneath it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79633, 'enUS', 'Then I will do my best.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79634, 'enUS', 'You\'d better hurry!  Here they come!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79636, 'enUS', 'You mustn\'t awaken the master!', 'You mustn\'t awaken the master!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79637, 'enUS', 'Kill them!', 'Kill them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79638, 'enUS', 'Hold your position, Frostwolves! Let them come to you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79639, 'enUS', 'Bring me more! Your Iron Horde can bleed on my axe!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79640, 'enUS', 'Axes up! Here they come again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79641, 'enUS', 'We can\'t fight with this cannon fire coming down. Thrall! $p! Take care of that tank!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79642, 'enUS', 'Dabu!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79643, 'enUS', 'You fight like no shaman I have ever seen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79644, 'enUS', 'I was taught by the best... Drek\'thar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79645, 'enUS', 'You should tell your teacher to rely less on the hammer, and more on his brains.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79646, 'enUS', 'He would be pleased to hear you say that.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79647, 'enUS', '...no, not really.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79648, 'enUS', 'Get back here! They\'ll be rallying again soon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79649, 'enUS', 'Durotan, this isn\'t a choke point... it\'s a meat grinder!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79650, 'enUS', '', '...and yet they still they keep coming.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79651, 'enUS', 'I know! That\'s the best part!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79652, 'enUS', 'Durotan! I need help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79653, 'enUS', 'Drek\'Thar\'s in trouble. Ga\'nar, get out there and lend him a hand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79654, 'enUS', 'Thought you\'d never ask, brother.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79655, 'enUS', 'You go too, $p.  Make sure my brother doesn\'t get himself killed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79656, 'enUS', 'I can still hear you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79657, 'enUS', 'Thank you. I owe you my life.', 'Thank you. I owe you my life.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79658, 'enUS', 'Save the embraces for later, shaman. I\'ve got a kill streak going.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79659, 'enUS', 'Fall back! Prepare for the next attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79660, 'enUS', '', 'I smell Thunderlords...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79661, 'enUS', 'Spears incoming! Hold your ground!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79662, 'enUS', 'Here comes another elekk!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79663, 'enUS', '', 'Another elekk on the way!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79664, 'enUS', 'Durotan! Tell your pups to come out of the kennel!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79665, 'enUS', 'Hero, approach my totem for aid!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79666, 'enUS', '', 'This one\'s mine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79667, 'enUS', 'I\'ll be waiting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79668, 'enUS', '', 'Come back me up, $p.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79669, 'enUS', 'Look at this cute little puppy you\'ve sent me, Durotan! Does she know any tricks?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79670, 'enUS', '', 'Yeah. A few.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79671, 'enUS', 'Come here, puppy. I want to play FETCH.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79672, 'enUS', '', 'Well... if it\'ll make you happy, then LET\'S PLAY!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79673, 'enUS', 'Draka... come join me in the Iron Horde.  Lose that armor, and settle down with a REAL MAN.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79674, 'enUS', '', 'A real man treats his mate with respect!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79675, 'enUS', '', '...and he probably would have caught that axe.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79676, 'enUS', 'Frostwolves! Stand strong! The battle is nearly done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79677, 'enUS', 'Drek\'Thar! How much time do you need?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79678, 'enUS', 'Only a bit longer!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79679, 'enUS', 'We are counting on you, shaman!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79680, 'enUS', 'Chieftain, do you hear that rumbling?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79681, 'enUS', 'I do. Something big is coming.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79682, 'enUS', 'Good. I\'ve been waiting for the big one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79683, 'enUS', 'GRONN!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79684, 'enUS', 'All Frostwolves - charge ahead! Take that thing down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79685, 'enUS', 'Lok\'tar ogar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79686, 'enUS', 'For the Frostwolves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79687, 'enUS', '', 'For our home! For our family!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79688, 'enUS', '', 'I\'m alive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79689, 'enUS', 'If the Thunderlords are to be believed, then the Iron Horde is on its way. It falls on us to prevent them from taking Frostfire Ridge by force.$b$bWe should move forward and defend our homeland at once!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79690, 'enUS', 'Let\'s move forward.', 'Let\'s move forward.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79691, 'enUS', 'Draka, make your way to the cave the scout spotted. I will meet the commander at the coastal village.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79692, 'enUS', 'Champion, we will meet you ahead when you are done with your mission.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79693, 'enUS', '', 'Great Prophet! Ner\'zhul is taking the captives to Shaz\'gul.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79694, 'enUS', 'BWAAAR! Work harder filthy slave things!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79695, 'enUS', 'Puny workers make me so ANGRY! RARGH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79696, 'enUS', 'Work or me gonna bash yer skulls!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79697, 'enUS', 'P-please... somebody... anybody...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79698, 'enUS', '', 'Lead it away from Drek\'Thar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79699, 'enUS', 'Light p-protect you...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79700, 'enUS', 'Returning to job, infirmary.', 'Returning to job, infirmary.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79701, 'enUS', 'Going on break.', 'Going on break.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79702, 'enUS', 'I will save my clan, even if I must forfeit the souls of my ancestors.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79703, 'enUS', 'Without you, I would still be in that jungle, or worse.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79704, 'enUS', 'Let\'s go meet Durotan.', 'Let\'s go meet Durotan.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79705, 'enUS', '', 'We have to stop Teron\'gor and his Bonechewer allies from breaking into Auchindoun at all costs.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79706, 'enUS', 'Exurotus is too weak. The most I can do is free him of his bonds. Death will claim him shortly afterward.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79707, 'enUS', 'If I were stronger...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79708, 'enUS', '', 'There must be something you can do.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79709, 'enUS', 'The fury is dying. We don\'t have much time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79710, 'enUS', 'There might be something. Exurotus, I offer my spirit as host to your own. Join with me, so that your fire may not be quenched.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79711, 'enUS', '', 'I\'m glad you\'re here, $n. Our forces are stretched thin. I need someone I can count on.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79712, 'enUS', 'MY SPIRIT... BURNS!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79713, 'enUS', '', 'Drek\'Thar, what has it done to you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79714, 'enUS', 'He has become one with the fury. Their spirits are now merged.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79715, 'enUS', '', 'The Auchenai are not prepared to defend against an assault like this. Auchindoun is a mausoleum, not a fortress. They need our help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79716, 'enUS', 'I... I can see. I can see the shift of the elements, the movement of the spirits through the land. It is glorious.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79717, 'enUS', '', 'If Teron\'gor devours the souls within, the results would be catastrophic.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79718, 'enUS', 'The ogres will suffer for what they\'ve done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79719, 'enUS', 'We are not prepared to defend against an assault like this. Auchindoun is a mausoleum, not a fortress.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79720, 'enUS', '$n, we must stop Teron\'gor and his Bonechewer allies from breaking into Auchindoun at all costs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79721, 'enUS', 'If Teron\'gor devours the souls within, the results would be catastrophic.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79722, 'enUS', 'You are too late, hero. Your world will be cast into shadow!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79723, 'enUS', 'I\'m ready to go, Exarch.', 'I\'m ready to go, Exarch.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79724, 'enUS', 'Light bless you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79729, 'enUS', 'Help me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79730, 'enUS', 'Coming, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79731, 'enUS', 'Is he dead? I think he was saving me for later. Come on Icepaw, let\'s get out of here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79732, 'enUS', 'The last time I saw Orac, he was chasing Icepaw out the southern gates of Wor\'gol.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79733, 'enUS', 'You should have seen it uncle! I had that filthy Saberon right where I wanted him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79734, 'enUS', 'Is that right? Good job, Orac. You will make a fine hunter one day.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79735, 'enUS', 'None escape the Void!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79736, 'enUS', 'We will secure our borders!', 'We will secure our borders!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79737, 'enUS', 'Once Master Blackheart\'s portal is active, he will come to take the key to the Master.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79738, 'enUS', 'The Master needs the key fragment. We will protect it until Lord Blackheart arrives!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79739, 'enUS', 'This world will be ours, just as the Master foretold.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79740, 'enUS', 'This wolf has been gutted and left to rot.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79741, 'enUS', '', 'Your pitiful weapons are useless against our Goliath Cannon!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79742, 'enUS', '', 'How did you survive the Goliath Cannon?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79743, 'enUS', '', 'Our Goliath Cannon will destroy all that you hold dear.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79755, 'enUS', 'Darkness is my strength! With this power, the Shadowmoon will be conquerors!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79756, 'enUS', 'Huh? I must have passed out. I suppose a warm fire and hot meal with my family wouldn\'t be so bad.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79757, 'enUS', 'Flowers?! Noooooo!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79758, 'enUS', 'Please... no flowers...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79759, 'enUS', 'Not me! Not now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79760, 'enUS', 'Not like this...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79761, 'enUS', 'The legends are true!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79762, 'enUS', 'Anything but flowers!', '', 26124);
