INSERT INTO `broadcast_text_locale` VALUES (79779, 'enUS', '', 'On behalf of the Frostwolf clan, I greet you, $c.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79780, 'enUS', 'The pain of this stake is nothing compared to that of my failure...', '', 26124);
