INSERT INTO `broadcast_text_locale` VALUES (79786, 'enUS', 'They were going to eat me!', 'I thought I was done for! Thanks pal!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79787, 'enUS', 'Those dirty bags of fur came out of nowhere!', 'How could this happen to me?!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79788, 'enUS', 'Ahhh!!!!! Finish it! Just let me die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (79789, 'enUS', 'Emma was here recently. Follow her trail up the hill!', 'Emma was here recently. Follow her trail up the hill!', 26124);
