INSERT INTO `broadcast_text_locale` VALUES (80215, 'enUS', 'Please find my other three friends! If you save them, I will gladly reward you when I return to Light\'s Rest.', 'Please find my other three friends! If you save them, I will gladly reward you when I return to Light\'s Rest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80216, 'enUS', 'My other two friends are still out there... please find them, hero.', 'My other two friends are still out there... please find them, hero.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80217, 'enUS', 'Anyway, I can find my way back to Light\'s Rest, but one of my party is still missing. Please find them!', 'Anyway, I can find my way back to Light\'s Rest, but one of my party is still missing. Please find them!', 26124);
