INSERT INTO `broadcast_text_locale` VALUES (80219, 'enUS', 'Please find my other three friends! If you save them, I will gladly reward you when I return to Sunsworn Camp.', 'Please find my other three friends! If you save them, I will gladly reward you when I return to Sunsworn Camp.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80220, 'enUS', 'My other two friends are still out there... please find them, hero.', 'My other two friends are still out there... please find them, hero.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80221, 'enUS', 'Anyway, I can find my way back to Sunsworn Camp, but one of my party is still missing. Please find them!', 'Anyway, I can find my way back to Sunsworn Camp, but one of my party is still missing. Please find them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80222, 'enUS', 'You found the others? Thank goodness! Come see us at Sunsworn Camp. I\'m sure we can find some way to thank you.', 'You found the others? Thank goodness! Come see us at Sunsworn Camp. I\'m sure we can find some way to thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80223, 'enUS', '', '\'Let\'s explore the old mine,\' she said. \'I need a sample for research,\' she said. Oh, why did I ever listen to Norana?', 26124);
