INSERT INTO `broadcast_text_locale` VALUES (80226, 'enUS', '', 'One of those green spiders bit me, but it was just a scratch. No pain, no fame, am I right?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80227, 'enUS', '', 'Guys? I... I don\'t feel... so good...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80228, 'enUS', 'I will defend my lands and my people, whatever the cost.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80229, 'enUS', 'I\'m with you! [Begin Scenario]', 'I\'m with you! [Begin Scenario]', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80230, 'enUS', 'I... already told you... all that I know...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80231, 'enUS', 'Do you even know how to wield a weapon!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80232, 'enUS', 'Easy as taking sweets from a draenei.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80233, 'enUS', 'This belongs to me now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80234, 'enUS', '', 'Guys? I... I don\'t feel... so good...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80235, 'enUS', '', 'I\'ve known Isaari since she was a little girl. She\'s always getting me into trouble!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80236, 'enUS', 'I can still hear them... chittering and clacking to one another...$B$B<Doruun shudders.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80237, 'enUS', 'Well, I\'m glad we escaped in one piece.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80238, 'enUS', 'Well, I\'m glad we escaped in one piece.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80239, 'enUS', 'I can still hear them... chittering and clacking to one another...$B$B<Orian shudders.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80240, 'enUS', '', 'That\'s it! I\'m never going on another one of Norana\'s stupid expeditions ever again!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80241, 'enUS', 'I see you are all safe.', 'I see you are all safe.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80242, 'enUS', 'Thanks to you, $n, we surviv-- Isaari? Are you all right?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80243, 'enUS', 'Thanks to you, $n, we surviv-- Norana? Are you all right?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80244, 'enUS', 'Bleed for me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80245, 'enUS', 'I\'ll roast your flesh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80246, 'enUS', 'We continue to gather more information on the frost wolf clan and their pathetic attempts at survival.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80247, 'enUS', 'Anything?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80248, 'enUS', 'Do you see anything?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80249, 'enUS', 'Snow... I see lots of snow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80250, 'enUS', 'Bah... this is a waste of time. Goreseeker has no idea what he is doing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80251, 'enUS', 'Umm... Yeah. We are doing great. Tell Goreseeker we are really gathering some great intel out here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80252, 'enUS', 'Excellent. I\'ll let Goreseeker know.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80253, 'enUS', 'It\'s happening just ahead, in the bottom of the tomb. I\'ve got to get out of here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80254, 'enUS', 'Igniting Barrel', 'Igniting Barrel', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80255, 'enUS', '$n, you are back! Please take the chest in this cart. It is only a small token of thanks for saving my life.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80256, 'enUS', 'I\'m da best bestest beater der is! I\'m gonna beat ya till ya beaten.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80257, 'enUS', 'Hey $n, remember me? You saved my life at the Court of Souls! Take this chest behind me as a token of my thanks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80258, 'enUS', 'What the? Shtop right there! I\'m going to kick your...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80259, 'enUS', '<BLAAAAARG!!!>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80260, 'enUS', 'You think you can beat me!??? I\'ll shhlap you right in your shtupid face, $c!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80261, 'enUS', 'He just slipped off the bridge?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80262, 'enUS', '', 'I\'ll keep the portal open as long as I can!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80263, 'enUS', 'Yup.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80264, 'enUS', '', 'Go now, while I can still hold it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80265, 'enUS', 'What a fool... Well, let\'s pick up these supplies and head back to base.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80266, 'enUS', 'You have done well, Soulbinder.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80267, 'enUS', 'Come, Teron. Auchindoun awaits. Unthinkable power will soon be yours.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80268, 'enUS', 'The Iron Horde will not hesitate to use that cannon on our base. We should find a more tactical location.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80269, 'enUS', 'Is everyone alright?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80270, 'enUS', 'I think I soiled m\'self...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80271, 'enUS', 'My ALE was back there...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80272, 'enUS', 'Yes, everyone appears to be alright.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80273, 'enUS', 'Very well. We will set up camp ahead, and deal with this cannon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80274, 'enUS', 'Yrel will have to wait.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80275, 'enUS', 'You are ready, Hurkan. Slay these mortals in my name!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80276, 'enUS', 'I have souls to devour.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80277, 'enUS', 'MY CLAN FOR GOREFIEND!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80278, 'enUS', 'Nixxie is dead? Right on! She couldn\'t manage her way out of a paper bag. Let\'s get out of here boys!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80279, 'enUS', '', 'Get it together you bums! It\'s bad Kal\'rak ditched out on us. But that doesn\'t mean you can start slacking.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80280, 'enUS', '', 'How many times have I told you! That keg is going to go bad if you keep it out  like that!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80281, 'enUS', '', 'This Iron Horde is paying us good money to transport these goods on time. So put some elbow grease into it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80282, 'enUS', 'You won\'t get away with this.', 'You won\'t get away with this.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80283, 'enUS', '', 'Stupid Kal\'rak... I\'m going to report that idiot to the Iron Horde\'s HR department. Assuming they have a HR department.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80284, 'enUS', '', 'I can\'t... hold it... much longer...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80285, 'enUS', '', 'I should have kept my job at the Kaja\'Cola factory in Azeroth.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80286, 'enUS', '', 'I don\'t care what you think! I\'m the lead here. Now get back to work!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80287, 'enUS', '', 'The wagon gets attacked by a giant insect, and what does the big, strong, Iron Horde orc do?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80288, 'enUS', '', 'He runs off with a keg of brew. Sheesh...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80289, 'enUS', '', 'Hey! What are you doing here?! Don\'t even think about touching our cargo!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80290, 'enUS', 'I\'m outta here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80291, 'enUS', 'Ding-Dong! Nixxie is dead!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80292, 'enUS', 'Finally we are free!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80293, 'enUS', 'Great to see you $R, we could use your help right away!', 'Great to see you $R, we could use your help right away!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80294, 'enUS', 'Let\'s start construction!', 'Let\'s start construction!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80295, 'enUS', 'Ready to get this garrison started?', 'Ready to get this garrison started?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80296, 'enUS', 'Everybody in the Tank. Yrel is being held as a slave in the Stonemaul ogres\' slave camp.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80297, 'enUS', 'Yrel might already be free if it weren\'t for the actions of one deceitful rangari.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80298, 'enUS', 'I did what I thought was right, Vindicator. The missing girl seems to be distracting you from our mission.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80299, 'enUS', 'Yeah, what\'s so special about this girl anyway?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80300, 'enUS', 'I lost her once. I will not lose her again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80301, 'enUS', 'Now THAT\'S somethin\' I can get behind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80302, 'enUS', 'Besides, the ogres\'re sellin\' slaves to the Iron Horde. Stoppin\' ta beat \'em up fer a bit is well within the scope o\' our mission.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80303, 'enUS', 'You all is gonna get smashed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80304, 'enUS', 'Oh no, ogre, that\'s where you have it wrong. You see, we are the ones that will be doing the smashing today. Thaelin?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80305, 'enUS', 'Aye aye, cap\'n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80306, 'enUS', 'Now is our chance! Forward, Thaelin, with speed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80307, 'enUS', 'What da...?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80308, 'enUS', 'Stay silent, all of you! Ogre, if you so much as GRUNT, I will personally beat the Light out of you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80309, 'enUS', 'We can set up over there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80310, 'enUS', 'Ok\'mokk didn\'t make no sounds at all. You don\' need ta smash Ok\'mokk.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80311, 'enUS', 'Ach, I have a feelin\' yer gonna get smashed anyway.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80312, 'enUS', 'A direct attack. A bold decision.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80313, 'enUS', 'Gathering allies. Wise decision.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80314, 'enUS', 'Form up, everyone. We are headed to Smokebelcher Depot.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80315, 'enUS', 'Form up, everyone. We are headed to the Planteau. [TEMP NAME]', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80316, 'enUS', '', 'Room for one more on that elekk?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80317, 'enUS', 'Always, my love.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80318, 'enUS', 'Yrel, did you learn anything about the Goliath Cannon before your capture?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80319, 'enUS', '', 'A bit. The one you destroyed was merely a prototype. The actual cannons are built in the foundry, to the north.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80320, 'enUS', '', 'I overheard one of the orcish leaders discussing a plan to use them on Shattrath.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80321, 'enUS', 'Shattrath? Ha! Even that big ol\' hunk o\' metal couldn\'t hit Shattrath from here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80322, 'enUS', 'They could if it was loaded onta\' a boat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80323, 'enUS', 'I suspected the same thing. They must plan to load the cannons onto barges at the Naval Base, in the northeast.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80324, 'enUS', 'Our small group would never survive an attack on the Naval Base. Too fortified.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80325, 'enUS', 'We will continue attacking their supply lines.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80326, 'enUS', '', 'D\'kaan, how long have you been scouting out here in Gorgrond?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80327, 'enUS', 'A very long time. Longer than you\'ve been alive, probably by two or threefold.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80328, 'enUS', 'D\'ye get lonely?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80329, 'enUS', 'A rangari who is not lonely is not doing his job.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80330, 'enUS', 'Sounds like a crap job to me. Life ain\'t worth livin\' if y\'don\'t have lads and a lass t\'come home to.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80331, 'enUS', '', 'Do you have a wife, Hansel?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80332, 'enUS', 'Ach... the Blackrocks took \'er away.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80333, 'enUS', 'That\'s half o\'why I\'m here: ta look after Thaelin, and ta get me wife back.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80334, 'enUS', 'We will find your wife, Hansel. And we will leave a wake of bodies behind us. The orcs will pay with blood for what they did to our families.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80335, 'enUS', 'Y\'know, that\'s the darkest, coldest, most inspirin\' thing I\'ve ever heard from the lips of a paladin.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80336, 'enUS', 'Our work here is done. Next, we head to Smokebelcher Depot.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80337, 'enUS', 'Our work here is done. Next, we head to the Planteau.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80338, 'enUS', '', 'Thaelin, are you doing alright in there?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80339, 'enUS', 'Quite fine, ma\'am. Just happy to be out o\' combat and back in me Tank.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80340, 'enUS', '', '<sigh> This land is plagued by war. Do you think you\'ll return to the safety of home after our mission is done?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80341, 'enUS', 'I dunno how much you\'ve heard about Azeroth, ma\'am, but we\'ve got our fair share of war, too.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80342, 'enUS', 'Once I\'ve found me Ellie, I know I\'ll be headed back ta Searin\' Gorge.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80343, 'enUS', 'What about you, lassie? Will ye go home?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80344, 'enUS', '', 'With Velen gone, I feel like I don\'t have a home anymore.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80345, 'enUS', 'We are draenei, Yrel. Being without a home is in our nature.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80346, 'enUS', '', 'I was thinking I might go through the portal with you, my love.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80347, 'enUS', 'Yes, Yrel. You will have a home on Azeroth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80348, 'enUS', 'But first, we will make these orcs suffer for what they did to you. Their sins cannot be forgiven.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80349, 'enUS', '', 'Maraad, you\'re frightening me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80350, 'enUS', 'The Iron Horde\'s supply lines are now cut, however temporarily. We have forged our alliance with the Shapers.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80351, 'enUS', 'There will never be a better time than this. Let us head to the Naval Base and exact our vengeance on the Iron Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80352, 'enUS', '', 'What is our plan, Maraad?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80353, 'enUS', 'We will slay as many of the vile things as we can. We will burn their homes and show them fear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80354, 'enUS', 'I will make them feel the pain I felt when I lost you, Yrel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80355, 'enUS', '', 'Maraad... I am honored...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80356, 'enUS', '', '...but perhaps you are taking your vengeance too far. Have you forgotten what the Prophet taught us?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80357, 'enUS', 'The prophet is DEAD, Yrel. Look what his teachings did for him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80358, 'enUS', 'Ye\'ve gone too far, Maraad.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80359, 'enUS', 'The Light\'s left ye. Even I can see that.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80360, 'enUS', 'I carry no particular love for the Light, but I agree. You need to remember what you stand for, Vindicator.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80361, 'enUS', 'Are any of you more holy than me? I am the LEADER of this expedition, and I will not be questioned any further!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80362, 'enUS', 'The Light has not left me, Thaelin. No... the Light has finally shown me its true form.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80363, 'enUS', 'Vengeance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80364, 'enUS', 'I saw the naaru\'s vengeance in the Purge of Grommar. I was there as K\'ara herself unleashed holy death upon the filthy hordes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80365, 'enUS', '[UNUSED]', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80366, 'enUS', '', 'Maraad, my love. That\'s enough.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80367, 'enUS', 'Yrel...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80368, 'enUS', '', 'You have been through too much, Maraad. Your thirst for vengeance will only lead to our death.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80369, 'enUS', '', '[UNUSED]', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80370, 'enUS', '', 'Rest now. I will lead our attack on the Naval Base.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80371, 'enUS', 'I\'m with you, Yrel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80372, 'enUS', 'I, too, will follow you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80373, 'enUS', 'And I.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80374, 'enUS', 'Very well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80375, 'enUS', 'Thank you, Yrel. I don\'t know what\'s coming over me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80376, 'enUS', 'Shutting Down', 'Shutting Down', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80377, 'enUS', 'Pull the first lever to increase combustion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80378, 'enUS', 'Pull the second lever to overdrive the engine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80379, 'enUS', 'Pull the third lever to destroy the Ultrabliterator!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80380, 'enUS', 'Stay back! It\'s on a rampage!', 'Stay back! It\'s on a rampage!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80381, 'enUS', 'Well done, champion! We will do our best to repair this unit. Thank you.', 'Well done, champion! We will do our best to repair this unit. Thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80382, 'enUS', '', 'We are all entitled to our own opinion. Yours is just wrong. NOW GET BACK TO WORK!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80383, 'enUS', 'Sorry! I just work here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80384, 'enUS', 'Whoa! What did I do?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80385, 'enUS', 'Nixxie! Help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80386, 'enUS', 'I should have never left Azeroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80387, 'enUS', 'Can\'t we talk this through?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80388, 'enUS', 'Lok-Narash!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80389, 'enUS', 'Gazlowe, buy us some time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80390, 'enUS', 'We will take the east!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80391, 'enUS', 'Orgrim, you will answer for your betrayal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80392, 'enUS', 'Brother!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80393, 'enUS', 'Auchindoun will soon be ours. Too bad you won\'t be around to see it, elf.', 'Auchindoun will soon be ours. Too bad you won\'t be around to see it, elf.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80394, 'enUS', 'Innocent?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80395, 'enUS', 'Yes, I will see Draenor burn. You, traitor, will not.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80396, 'enUS', '', 'Lok\'tar ogar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80397, 'enUS', '', 'I will hold off reinforcements at the docks!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80398, 'enUS', '', 'I will get the children to safety!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80399, 'enUS', 'You got it, boss!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80400, 'enUS', 'Together, we fight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80401, 'enUS', 'Prepare for a counter-attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80402, 'enUS', 'They\'re retreating to the harbor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80403, 'enUS', 'The children are safe! Their lieutenants are exposed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80404, 'enUS', 'Only the harbor remains between us and the weapon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80405, 'enUS', 'Champion! Speak with me when you are ready to travel to the enemy battleship.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80406, 'enUS', 'I will not slay so many innocent people!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80407, 'enUS', 'It is YOU who would make Draenor burn. It is YOU who must be stopped!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80408, 'enUS', 'For Shattrath!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80409, 'enUS', '', 'Thaelin, use your tank to save them! We will provide escort.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80410, 'enUS', '', 'We will hold off Blackhand! Destroy that weapon!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80411, 'enUS', '', 'They have children in those buildings!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80412, 'enUS', '', 'We will take the west!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80413, 'enUS', 'Dismantling', 'Dismantling', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80414, 'enUS', '%s hisses, slinking back into the shadowy depths!', '%s hisses, slinking back into the shadowy depths!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80415, 'enUS', 'A chill wind howls across the empty hills...', 'A chill wind howls across the empty hills...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80416, 'enUS', 'A chittering sound echoes across the trees, then fades...', 'A chittering sound echoes across the trees, then fades...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80417, 'enUS', 'Something flickers in the shadows, growing closer...', 'Something flickers in the shadows, growing closer...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80418, 'enUS', 'We don\'t want any trouble, $c. We stopped fighting a long time ago.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80419, 'enUS', 'I could eat an orc.', 'I could eat an orc.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80420, 'enUS', 'When you live in a savage world like we do, sometimes you just want to get away.$b$bThat\'s why we left the war behind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80421, 'enUS', 'Feel free to take some fish. They are delicious.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80422, 'enUS', '|TInterface\\Icons\\ability_fixated_state_red:20|tA %s |cFFFF0000|Hspell:156653|h[Fixates]|h|r on you. Run!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80423, 'enUS', 'Stop! Who goes there? Go away before I put an arrow in your eye!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80424, 'enUS', 'Pardon my rudeness, traveler. I did not know you from the enemy.$B$BThe Shadow Council has tried to raid my home several times now.$B$BThe first time, they didn\'t expect much resistance from an old hermit like me. I put arrows into the throats of three.$B$BThe second time, I was prepared. Four orcs blundered right into my traps. Stupid mongrels.$B$BI made them angry, and now, I\'m waiting for them to come again.$B$BI hope they send more. It just means that many more will die.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80425, 'enUS', 'Do you need help?', 'Do you need help?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80426, 'enUS', 'Stay if you want, traveler. I\'ll die before letting these dogs take my home. Watch out! They come!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80427, 'enUS', 'Heh, you\'ve got some fight in you, traveler. Ready for more?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80428, 'enUS', 'This is it, friend. Let\'s cut them off at the head!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80429, 'enUS', 'We did it! I... I can\'t thank you enough, stranger. I don\'t have much, but I want you to have this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80430, 'enUS', '', 'The hunt comes to an end. Quickly, drink this antidote! The spider\'s venom will no longer affect you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80431, 'enUS', 'Are you sure this is a good idea?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80432, 'enUS', 'If you want something done, you have to do it yourself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80433, 'enUS', '', 'There is another I hunt within these caves. I leave this kill to you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80434, 'enUS', 'Find a way to destroy the Supply Depot!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80435, 'enUS', 'The Sky Rider\'s have noticed your presence!', 'The Sky Rider\'s have noticed your presence!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80436, 'enUS', '', 'Rise and serve your master!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80441, 'enUS', 'Our clan grows stronger by the second.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80442, 'enUS', 'Careful - we have a tough enemy cornered in there.', 'Careful - we have a tough enemy cornered in there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80443, 'enUS', 'Grulk gonna pound you all!! Rargrh!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80444, 'enUS', 'Feel weak...Grulk need serum...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80445, 'enUS', 'Grulk Smash!! Rarggah!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80446, 'enUS', 'Grulk so tired...just want nap.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80447, 'enUS', 'Grulk is strongest one there is!! Rrrrgh!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80448, 'enUS', 'This place where Grulk change...where old self die...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80449, 'enUS', 'NO MORE!! GRULK CRUSH!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80450, 'enUS', 'Are you sure this is a good idea?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80451, 'enUS', 'Yeah, you\'ll be fine. I just forgot to adjust my sights last time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80455, 'enUS', '', 'Keep your eyes open... The stones speak of trouble...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80456, 'enUS', 'Hunters, arm your traps. I smell something...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80457, 'enUS', 'Another trophy for the pen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80458, 'enUS', 'The stones spoke of your demise.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80459, 'enUS', 'Who dares wake me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80460, 'enUS', 'You are weak.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80461, 'enUS', '', 'I see you, interlopers. You tread on sacred ground...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80462, 'enUS', '', 'Insignificant whelps, what makes you think you can challenge me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80463, 'enUS', '', 'I will turn you into ash!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80464, 'enUS', '', 'Drown in venom!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80465, 'enUS', '', 'The undertow will consume you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80466, 'enUS', '', 'Hah! My power rules here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80467, 'enUS', '', 'Agamaggan will rise again!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80469, 'enUS', 'Auchindoun... is... doomed...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80470, 'enUS', 'None live who assault the holy Auchenai.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80471, 'enUS', 'I will strike you down.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80472, 'enUS', 'The Light guide my hand!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80473, 'enUS', 'Light guide you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80474, 'enUS', 'Bathe in the glory of the Light!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80475, 'enUS', 'I don\'t know... Roc\'tar doesn\'t look so good.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80476, 'enUS', '', 'It is my sworn duty to protect this tomb and its grounds.$B$BWe will fight to the last to prevent these orcs from taking the Shard of Souls!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80477, 'enUS', 'Soulbinder Nyami sent me to bring the Shard of Souls to safety.', 'Soulbinder Nyami sent me to bring the Shard of Souls to safety.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80478, 'enUS', '', 'Protect that with your life! We need it to power Auchindoun\'s defenses!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80479, 'enUS', 'Put me down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80480, 'enUS', 'I will give you a swift death!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80481, 'enUS', 'Drop me now you beast!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80482, 'enUS', 'Pathetic creature! Unhand me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80483, 'enUS', 'Grulk done wit\' Iron Horde...Grulk go now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80484, 'enUS', 'You friend to Grulk...Grulk not forget what you done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80485, 'enUS', 'I\'m strongest! This food is mine! Stay away!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80486, 'enUS', 'You no try and fight me! Now you food!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80487, 'enUS', 'I kill you! I eat you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80488, 'enUS', 'See what I have?! No mess with me! Or you next.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80489, 'enUS', '', 'Hold your ground. We have to protect the wounded!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80490, 'enUS', '%s calls in a distant |cFFFF0000Siegemaker|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80491, 'enUS', 'Your head will be mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80492, 'enUS', 'It is no matter. These souls are mine. My power grows.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80493, 'enUS', 'Auchindoun will be mine! I will devour every last soul and bathe the ashes in hellfire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80494, 'enUS', 'To arms, anchorites! The arakkoa are coming! Hero, lend us your aid!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80495, 'enUS', 'More birds on the horizon, my comrades. Send them back to the Spires of Arak, where they belong!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80496, 'enUS', 'Will their numbers never cease? Muster your courage, friends. We will not be taken so easily!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80497, 'enUS', 'Finally, we have drawn out their leader! Face us, you feathered tyrant!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80498, 'enUS', 'Our town is safe thanks to all of you. Today we have given the arakkoa a taste of our steel. Steady hearts, anchorites! We will stay vigilant.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80500, 'enUS', 'Welcome to Anchorite\'s Sojourn, traveler. It is here that the devout gather for pilgrimage to the Temple of Karabor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80501, 'enUS', '', 'And why do you think we need your help, \'blood elf\'? We Auchenai are sworn to Auchindoun\'s protection.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80502, 'enUS', 'Explosions, sabotage, demolition...all in a day\'s work friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80503, 'enUS', 'Defend the shard! It must reach Lord Teron\'gor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80504, 'enUS', 'Grommar is a perfect example of the Alliance not knowin\' how to get the job done right...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80505, 'enUS', 'I have seen your kind before, flying among the mountains to the south. Why have you come here?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80506, 'enUS', 'Auuugghhh! What have you done? The sun... it blinds me! It burns! IT BURNS!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80507, 'enUS', 'The darkness closes in. I can no longer feel the Light. YOU! You did this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80508, 'enUS', 'I alone will stand against you. I will turn you back from these lands!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80509, 'enUS', 'The old paladin suddenly gasps. A look of rapture crosses his face and he blinks, seeing you for the first time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80510, 'enUS', 'I... I can feel the Light again! I am freed of her curse. I... I see the way to Auchindoun.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80511, 'enUS', 'An arakkoa sun priest has come, seeking dominion over our lands.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80512, 'enUS', 'My village bled slowly. Many fell to his sorcery. I, too, fell under his abominable curse... the \"sun sickness.\"', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80513, 'enUS', 'We knew the sun priest would come one last time to claim his victory.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80514, 'enUS', 'I alone remained to face him. My sight was nearly gone, and the Light no longer answered my prayers.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80515, 'enUS', 'I prayed once last time, although where there had once been everlasting divinity, now, I felt only emptiness.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80516, 'enUS', 'In that moment, I knew true terror.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80517, 'enUS', 'I did not see the sun priest arrive, but I heard the flutter of his wings, and felt his gloating breath on my face, and then, moments later, felt him die on my blade.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80518, 'enUS', 'Afterward, I wandered. Blind and alone, and severed from the Light. Even in my dying moment, I felt I had been forsaken.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80519, 'enUS', 'You have delivered me from his curse. You have restored my faith, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80520, 'enUS', 'Take my sword, which rests upon the stone. May it guide you when your faith falters, hero.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80521, 'enUS', 'NOTUSED', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80522, 'enUS', '', 'Take one step closer and I\'ll gut you like a fish, orc!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80523, 'enUS', '', 'Thank the stars you arrived, hero! We are all that is left of Telmor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80524, 'enUS', '', 'Thanks pal! I\'d hug yah, but we don\'t have much time...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80525, 'enUS', '', 'Do me a favor and release Grulk from his cage...don\'t worry, he\'s cool.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80526, 'enUS', 'To smash? Or not to smash? Dis Grulk question.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80527, 'enUS', 'Something need smashing?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80528, 'enUS', '', 'Grulk was mutated against his will by the Iron Horde and then locked up for being angry over it. Can you believe that?!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80529, 'enUS', 'SMASH!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80530, 'enUS', 'Grulk rhyme now!$b$bSmash the baddies! Smash them up!$b$bSmash them till fit in soup cup!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80531, 'enUS', '', 'We gotta bust up this mutation laboratory before it\'s too late! Grulk will fill you in on the details.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80532, 'enUS', '', 'I\'d better meet back up with the squad. Regroup with us once you\'ve destroyed the lab!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80533, 'enUS', '', 'Yes, Tuulani. Now you see.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80534, 'enUS', '', 'Your efforts were valiant. I did not think the anchorites could power the defenses with only one of the crystals.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80535, 'enUS', '', 'But nothing will stop my plan, nor that of my masters. And the only things in our way...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80536, 'enUS', '', 'Protector! Destroy these intruders!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80537, 'enUS', 'Run for your lives!', 'Run for your lives!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80538, 'enUS', 'They\'re eating us alive... literally!', 'They\'re eating us alive... literally!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80539, 'enUS', 'Where are the Auchenai? Why is no one saving us?', 'Where are the Auchenai? Why is no one saving us?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80540, 'enUS', 'There\'s no hope! Run!', 'There\'s no hope! Run!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80541, 'enUS', 'They killed my family!', 'They killed my family!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80542, 'enUS', 'I\'m glad you showed up. We were going to be killed next!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80543, 'enUS', 'The creatures around here are a pain to deal with.$b$bI\'ve taken to placing some of my food and prized possessions up high to avoid any unwanted incidents.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80544, 'enUS', 'Wow! Thanks! I can find my way out of here now. I\'m sure I won\'t get captured again. See ya!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80545, 'enUS', 'Crush, crack, splinter! AHAHAHahahaha...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80546, 'enUS', 'Whispers... slithering behind my EYES!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80547, 'enUS', '$ns drop from the ceiling!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80548, 'enUS', 'No! The ritual is incomplete! Who dares to interrupt my arrival?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80549, 'enUS', 'My power... it fades!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80550, 'enUS', 'NO! Noooooooooooooooooooooooooooooooo!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80551, 'enUS', 'Gazlowe, get rid of that barricade!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80552, 'enUS', 'Gazlowe...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80553, 'enUS', 'I fear that even with the Heart intact, Auchindoun\'s defenses may not be enough. The Shadow Council has eyes everywhere.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80554, 'enUS', 'Barricade! On it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80555, 'enUS', '', 'Thaelin, get that tank down here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80556, 'enUS', 'It isn\'t safe to fire this thing near kids! But I bet the concussion from shooting blanks would blast open those doors...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80557, 'enUS', 'Champion, take control of the cannon! If they get close, use the flames... hahahahaha!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80558, 'enUS', 'Suit! Go home!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80559, 'enUS', 'There ye go... easy on the turn now... easy...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80560, 'enUS', 'GAH!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80561, 'enUS', 'Oh, ye poor thing. You\'ll not be forgotten!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80562, 'enUS', '', 'The key is in the chest by the wall, go grab it and let me out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80563, 'enUS', '', 'What are you staring at? Go grab the key from the chest and let me out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80564, 'enUS', '', 'Boy am I glad to see you! The key is in that chest over there, be a pal and let me out will yah?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80565, 'enUS', '', 'I\'m locked in here, you\'ll need to get the key. It\'s in the chest by the wall!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80566, 'enUS', '', 'Don\'t mind me, I\'ll just wait here in my cage while you continue NOT letting me out! The key is in that chest over there!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80567, 'enUS', '', 'This isn\'t some romantic \"rescue the princess from the tower\" situation. This is a torture room and I\'m in a freakin\' cage! Get me outta here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80568, 'enUS', 'Pledge yourself to the master and you will receive untold power!', 'Pledge yourself to the master and you will receive untold power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80569, 'enUS', 'Witness the infinite power of the Legion!', 'Witness the infinite power of the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80570, 'enUS', 'The draenei will crumble under our combined might, brothers and sisters!', 'The draenei will crumble under our combined might, brothers and sisters!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80571, 'enUS', 'Nothing can stop our power! Not the Iron Horde, not the draenei, we are invincible!', 'Nothing can stop our power! Not the Iron Horde, not the draenei, we are invincible!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80572, 'enUS', 'Give your souls to Teron\'gor. Gorge yourselves in his power!', 'Give your souls to Teron\'gor. Gorge yourselves in his power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80573, 'enUS', '', 'Hurry up... we have work to do.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80574, 'enUS', '', 'This is a waste of time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80575, 'enUS', '', 'Are you done yet?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80576, 'enUS', '', '*Sigh*', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80577, 'enUS', '', 'As for you beast, flee if you must, but my arrows will catch you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80578, 'enUS', 'I\'m almost done! Just one more time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80579, 'enUS', 'You have got to try this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80580, 'enUS', 'Don\'t knock it till you try it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80581, 'enUS', 'Come on! You might like it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80582, 'enUS', '', 'Die beast, and know that whatever hell you fall into will be more merciful than I!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80583, 'enUS', '', 'Thank you for your aid, adventurer. Meet me back at camp and we will discuss matters further.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80584, 'enUS', '', 'That Grulk guy sure is incredible, ain\'t he? Mutated against his will...out for revenge...HUGE rippling biceps...a girl can only dream.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80585, 'enUS', 'You need somethin\' done right? You\'re talkin\' to the right guy...but the right guy ain\'t cheap.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80586, 'enUS', 'Lok-Narash!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80587, 'enUS', 'No!!! I will tear you apart for that! You will be avenged, Gutgore!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80588, 'enUS', 'The thunderlord... will rise...  again...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80589, 'enUS', 'Mole machine, go home!', 'Mole machine, go home!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80590, 'enUS', 'Don\'t bite me! I will not harm you... Seriously, don\'t do it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80591, 'enUS', 'I should have stayed in Shadowmoon Valley.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80592, 'enUS', 'Please! I have so much more to see before I die.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80593, 'enUS', 'Thank you, friend! Till we meet again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80594, 'enUS', 'So much power! I am invincible!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80595, 'enUS', 'But the power... it protects me...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80596, 'enUS', 'You disappoint me, I thought you\'d be tougher.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80597, 'enUS', 'The weak should know better than to test me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80598, 'enUS', '%s begins to channel |cFFFF0404|Hspell:157059|h[Rune of Grasping Earth]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80599, 'enUS', 'The wormhole is spinning really fast.  You\'ll have to jump first and hope for the best.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80600, 'enUS', 'A jagged landscape', 'A jagged landscape', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80601, 'enUS', 'A reddish-orange forest', 'A reddish-orange forest', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80602, 'enUS', 'Shadows...', 'Shadows...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80603, 'enUS', 'Pick your second location.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80604, 'enUS', 'Too many Frostwolves have died in this war.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80605, 'enUS', 'I will not rest till the Thunderlords are destroyed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80606, 'enUS', 'I once hunted Clefthooves for meat and skins, now I hunt Thunderlords for their heads. Not much of a difference really.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80607, 'enUS', 'Grassy plains', 'Grassy plains', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80608, 'enUS', 'Primal forest', 'Primal forest', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80609, 'enUS', 'Lava and snow', 'Lava and snow', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80610, 'enUS', 'Location 07', 'Location 07', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80611, 'enUS', 'Location 08', 'Location 08', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80612, 'enUS', 'Location 09', 'Location 09', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80613, 'enUS', 'Location 10', 'Location 10', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80614, 'enUS', 'Location 11', 'Location 11', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80615, 'enUS', 'Location 12', 'Location 12', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80616, 'enUS', 'Got anything important to say or are you just wasting my time?', '', 26124);
