INSERT INTO `broadcast_text_locale` VALUES (80630, 'enUS', 'What happened to my furnace? Where are my guards? Shut these intruders down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80631, 'enUS', 'I\'ll sneak into the camp. When you finish I\'ll meet you at the rendezvous point I marked on your map.', '', 26124);
