INSERT INTO `broadcast_text_locale` VALUES (80633, 'enUS', 'Meat still looks too rare...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80634, 'enUS', 'You want a burger, $p?', '', 26124);
