INSERT INTO `broadcast_text_locale` VALUES (80637, 'enUS', '', 'Confound these wards! No matter how much I call upon the light to purge them it is as if the wards themselves thwart my ability to unmake them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80638, 'enUS', 'Looks like it still needs more heat...', '', 26124);
