INSERT INTO `broadcast_text_locale` VALUES (80784, 'enUS', 'Abandon Infernal', 'Abandon Infernal', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80785, 'enUS', 'Abandon Abyssal', 'Abandon Abyssal', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80786, 'enUS', 'Accept Shadow Hunter Rala\'s offer of service.', 'Accept Shadow Hunter Rala\'s offer of service.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80787, 'enUS', 'My sista have other duties, but I have leave of ol\' Vol\'jin to extend my services to you.$b$bYa proved your worth, and did a task that needed doing.$b$bI be followin\' ya, boss-mon, if ya be willing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80788, 'enUS', '|TInterface\\Icons\\ability_blackhand_marked4death:20|tYou are |cFFFF0000|Hspell:156096|h[Marked for Death]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80789, 'enUS', 'No! I must ascend!', 'No! I must ascend!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80790, 'enUS', 'The Light! It burns!', 'The Light! It burns!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80791, 'enUS', 'You will pay for this, mortal!', 'You will pay for this, mortal!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80792, 'enUS', 'What are you doing? Stop!', 'What are you doing? Stop!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80793, 'enUS', 'Stop! I must give myself to Teron\'gor!', 'Stop! I must give myself to Teron\'gor!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80794, 'enUS', 'Too little, too late. Arise!', 'Too little, too late. Arise!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80795, 'enUS', 'Your blessings stop nothing!', 'Your blessings stop nothing!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80796, 'enUS', 'I... am... LEGION!', 'I... am... LEGION!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80797, 'enUS', 'DIE!', 'DIE!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80798, 'enUS', 'Our power is too great for the likes of you.', 'Our power is too great for the likes of you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80799, 'enUS', '', '$n, speak to Choluna. Let\'s go investigate this... Cragplume Cauldron.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80800, 'enUS', '', 'Hang on tight, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80801, 'enUS', '', 'Here we are. What is this place?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80802, 'enUS', '', 'I\'ve always had a fascination for blending in with the locals, but the inside of this Iron Horde armor smells awful. Maybe they\'d be less angry if they took a bath once in a while...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80803, 'enUS', '', 'The Iron Horde should never be underestimated, $n. Although they wield weaponry and sizeable forces that are worthy of respect, it is their unwavering persistence that must never go unchecked.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80804, 'enUS', 'My trusty wrist-mounted, gnomatic cloaking device allows me to repair my gyrocopter unseen. An ingenious device if I do say so myself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80809, 'enUS', 'Borgal! Get back here, you murdering son of a trogg!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80810, 'enUS', 'Barrels are the operative\'s perfect disguise. Nobody ever suspects a barrel...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80811, 'enUS', '', 'The Iron Horde cannot hide from our sharp eyes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80812, 'enUS', '', 'I have never before seen such a large network of steam vents and craters. What a fascinating place to study!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (80813, 'enUS', 'Take me to Cragplume Cauldron.', 'Take me to Cragplume Cauldron.', 26124);
