INSERT INTO `broadcast_text_locale` VALUES (81319, 'enUS', '|TINTERFACE\\ICONS\\spell_Mage_NetherTempest.blp:20|t |cFFF00000|Hspell:158648|h[Orbs of Chaos]|h|r explode from $n!', '|TINTERFACE\\ICONS\\spell_Mage_NetherTempest.blp:20|t |cFFF00000|Hspell:158648|h[Orbs of Chaos]|h|r explode from $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81320, 'enUS', 'Receive my gift, Vorpil. You will be the first of a new breed of conquerors!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81321, 'enUS', '', 'What a curious specimen! Have you ever seen such a flower as this, traveler?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81322, 'enUS', 'Ah, the thorn in my side has arrived.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81323, 'enUS', 'It\'s time I taught you a lesson.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81324, 'enUS', 'Come, Cerus. Dinner awaits.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81325, 'enUS', '', 'Start Worldaction!', 26124);
