INSERT INTO `broadcast_text_locale` VALUES (81327, 'enUS', '', 'Terminate Worldaction!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81328, 'enUS', 'Let the ritual begin.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81329, 'enUS', 'Feast your eyes on my limitless power, mortal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81330, 'enUS', 'Spirit!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81331, 'enUS', 'Hold her! We can\'t let her escape before the ritual completes!', 'Hold her! We can\'t let her escape before the ritual completes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81332, 'enUS', 'We are almost finished! A little more and her power will be ours to command!', 'We are almost finished! A little more and her power will be ours to command!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81333, 'enUS', 'Hold steady!  We must consume our ancestor\'s power if we have any hope to join the Iron Horde.', 'Hold steady!  We must consume our ancestor\'s power if we have any hope to join the Iron Horde.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81334, 'enUS', 'IGNORE ME!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81335, 'enUS', 'Stables.', 'Stables.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81336, 'enUS', 'Remove Stables.', 'Remove Stables.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81337, 'enUS', 'Well, hero, I owe you both my life and my honor. I ask that you allow me to repay this debt by serving under your command.$b$bYou will find that I am a fearsome warrior. I have also mastered the art of riding the rylaks, which few among the Frostwolves can claim.$b$bWill you accept my service?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81338, 'enUS', 'Accept the Mulverick\'s Offer of Service. (Garrison Follower)', 'Accept the  Mulverick\'s Offer of Service. (Garrison Follower)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81339, 'enUS', 'Your deeds have shown you to be a fine commander. The sort of commander I would be honored to fight alongside.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81340, 'enUS', 'Run! %s has |cFFFF0000|Hspell:159632|h[Insatiable Hunger]|h|r and wants to eat you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81341, 'enUS', 'What are you doing here?', 'What are you doing here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81342, 'enUS', 'Run away... they\'ll torture you too...', 'Run away... they\'ll torture you too...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81343, 'enUS', 'Who... who are you...?', 'Who... who are you...?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81344, 'enUS', 'Just let me die already!', 'Just let me die already!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81345, 'enUS', 'Do it now, stranger! End my pain!', 'Do it now, stranger! End my pain!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81346, 'enUS', 'Need... water...', 'Need... water...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81347, 'enUS', 'What\'s happening? Who are you?', 'What\'s happening? Who are you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81348, 'enUS', 'I want my mama...', 'I want my mama...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81349, 'enUS', 'Here, take one of their blades. Don\'t use it until we give the signal.', 'Here, take one of their blades. Don\'t use it until we give the signal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81350, 'enUS', 'I lend you my strength, master!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81351, 'enUS', 'Lord Mongrethod, hear my call. This mortal threatens the master\'s plans!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81352, 'enUS', '', 'Eh boss-mon! Come in outta da cold. We be findin\' a right hot spot here in these rocks. Make ya feel right at home.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81353, 'enUS', 'words and things', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81355, 'enUS', 'A glorious battle awaits us, $p.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81356, 'enUS', 'Saberon! I smell your stink upon the shadows...come out and play!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81357, 'enUS', 'This had better work, Gul\'dan.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81358, 'enUS', 'Why do you torment us?', 'Why do you torment us?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81359, 'enUS', 'You have damned yourself and our clan.', 'You have damned yourself and our clan.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81360, 'enUS', 'You have defiled our sacred arts and for what?', 'You have defiled our sacred arts and for what?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81361, 'enUS', 'Hey! What you doing? Dems MY food!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81362, 'enUS', 'We do what we must to survive!', 'We do what we must to survive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81363, 'enUS', 'Do not judge me!  Our clan depends on the power your soul hides.', 'Do not judge me!  Our clan depends on the power your soul hides.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81364, 'enUS', 'I will enjoy twisting what remains of you.', 'I will enjoy twisting what remains of you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81365, 'enUS', 'I find no joy in this. Do not make this any harder than it already is.', 'I find no joy in this, do not make this any harder than it already is.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81366, 'enUS', 'Come, Teron\'gor. Our contact has provided another entrance to Auchindoun. Your feast of souls awaits.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81367, 'enUS', 'My... dinner....', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81368, 'enUS', 'Yes, Gul\'dan. Your will be done.', 'Yes, Gul\'dan. Your will be done.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81369, 'enUS', 'Not dying on my watch!', 'Not dying on my watch!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81370, 'enUS', 'Live!', 'Live!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81371, 'enUS', 'Gaahh! How me supposed to learn how to control da ice if you breaks me out of it! Me gonna smash you for dat!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81372, 'enUS', 'I t\'ink I\'m getting colder...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81373, 'enUS', 'I\'ve seen enough here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81374, 'enUS', 'Kargath, you coward!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81375, 'enUS', 'You wouldn\'t last ten seconds against me, draenei.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81376, 'enUS', 'You sound afraid, Kargath.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81377, 'enUS', 'Two on one? Now, you speak my language!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81378, 'enUS', 'That\'s not very sportsmanlike, Bladefist. Why don\'t we settle this in the arena?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81379, 'enUS', 'Hold everything! Now, they speak my language!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81380, 'enUS', 'But I\'m afraid you\'re not much more than a warm-up, draenei. Doesn\'t anyone else feel like dying today?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81381, 'enUS', 'Thought you\'d never ask.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81382, 'enUS', 'Return to Khadgar, champion! We will buy you the time you need!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81383, 'enUS', 'I\'d like to cut off that head of yours, draenei.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81384, 'enUS', 'Why don\'t you put your blade where your mouth is, Kargath?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81385, 'enUS', '... looks like somebody already put his blade where his hand was.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81386, 'enUS', 'I\'ll do one better and put my blade where your throat is!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81387, 'enUS', 'Do you always let a draenei fight your battles for you, green-skin?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81388, 'enUS', 'I do when we share a common enemy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81389, 'enUS', 'Ptuh! And I thought you were an orc...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81390, 'enUS', 'You wield a Blackrock hammer, green-skin.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81391, 'enUS', 'Yes, I do. Would you like to take a closer look?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81392, 'enUS', 'I love fighting draenei. You people always look so scared before you die.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81393, 'enUS', 'Sorry to disappoint you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81394, 'enUS', 'Mangy beasts! You fight like kittens!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81395, 'enUS', 'Come at me with tooth and claw!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81396, 'enUS', 'The blood of the innocent stains your claws!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81397, 'enUS', '%s flees to report your position!', '%s flees to report your position!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81398, 'enUS', 'The rest have fled...but not from us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81399, 'enUS', 'The air...it smells like...something is wrong.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81400, 'enUS', '', 'Asha! To me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81401, 'enUS', 'I\'ll help you!', 'I\'ll help you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81402, 'enUS', 'I will fight by your side.', 'I will fight by your side.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81403, 'enUS', '', 'Can you imagine a live colossus? They say this was the last one, and the Thunderlord clan took it down generations ago.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81404, 'enUS', 'I must help the others. Slay more of them for me!', 'I must help the others. Slay more of them for me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81405, 'enUS', 'I am here to end your suffering, friend. Go in peace, your tormentors will not go unpunished.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81406, 'enUS', 'Wipe out the Unmanned Shredders and meet us on the south side of the island.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81407, 'enUS', 'Ha, we\'ll kill them yet!', 'Ha, we\'ll kill them yet!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81408, 'enUS', 'Death to our enemies!', 'Death to our enemies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81409, 'enUS', 'Well fought!', 'Well fought!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81410, 'enUS', 'It\'s time for the Iron Horde to learn a little respect for the hunt. Haratha, see to it that our friend here is protected.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81411, 'enUS', 'When I said I\'d help with the war effort I thought I\'d be a soldier helping win the war through skill with a weapon.$bInstead I\'m here drawing up plans and placing bricks.$b$bI guess I\'ll live longer this way at least.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81412, 'enUS', 'As promised I will set you free. But I think these wolves would like to talk with you before you go.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81413, 'enUS', 'Let\'s go, $n. Our hunt continues.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81414, 'enUS', 'Whoa! Hold on! Now, just wait a second! We can talk this through!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81415, 'enUS', 'You fight with honor, $p.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81416, 'enUS', 'I will tell my brethren at the arena of your courage. Be safe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81417, 'enUS', 'Somebody help!!! I need back up!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81418, 'enUS', 'I feel the flames dying down. What is happening, exarch?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81419, 'enUS', 'We have a visitor. Perhaps... a savior.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81420, 'enUS', 'We are freed! Follow me, shaman. We will escape through the hills.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81421, 'enUS', 'Steady...  steady. You are no longer in danger. I will not hurt you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81422, 'enUS', 'I will fly her to safety. Meet me at the islands east of here. Nisha will fly you there if you wish.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81423, 'enUS', 'Whoa! There you go... good girl.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81424, 'enUS', 'Eye of Gul\'dan has spotted you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81425, 'enUS', '%s plants an Ironmarch War Banner!', '%s plants an Ironmarch War Banner!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81426, 'enUS', 'Teron\'gor does not desire further disturbances. Dispose of the intruder!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81427, 'enUS', 'I\'m ready to choose the outpost building now.', 'I\'m ready to choose the outpost building now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81428, 'enUS', '', 'This is where it happened...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81429, 'enUS', '', 'Look here - wolf tracks. They turn into orc tracks. One of our shamans found her.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81430, 'enUS', 'Where is her body?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81431, 'enUS', 'The Iron Horde has been feeding these wolves to the rylaks.$b$bThis little runt was trying to get away when I broke the gate open.$b$bLet\'s see if he is of any use to us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81432, 'enUS', '', 'Whoever it was, they must have recovered Asha\'s body.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81433, 'enUS', '', 'They will give her the proper last rites. Come, let us speak with Durotan.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81434, 'enUS', 'IGNORE ME!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81435, 'enUS', 'I ain\'t giving up easy! Garrosh personally promised me Stormwind!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81436, 'enUS', 'Hey Franz? Franzok! We got a jam!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81437, 'enUS', 'I ain\'t un-jammin it! I un-jammed it last time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81438, 'enUS', 'Ey you! Get in dere and un-jam that stamper!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81439, 'enUS', 'So? Go un-jam it den!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81440, 'enUS', 'You wanna tell Blackhand we got a stamper that ain\'t stampin\'?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81441, 'enUS', 'Ha ha ha! Guess dis batch will have some impurities.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81442, 'enUS', 'Work work work...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81443, 'enUS', 'He is right. The Iron Horde will move against us. We must be ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81444, 'enUS', 'We\'ll be ready. They will NEVER take the pass.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81445, 'enUS', '', 'Are you okay, husband?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81446, 'enUS', 'No, I will never be okay with this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81447, 'enUS', 'He made his choice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81448, 'enUS', 'Come! To Thunder Pass. We are outnumbered 50 to 1. They don\'t stand a chance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81450, 'enUS', '<Ared\'s breath comes in short gasps as he lays in an uneasy sleep.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81451, 'enUS', 'Examine Ared\'s condition more closely.', 'Examine Ared\'s condition more closely.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81452, 'enUS', '<Ared\'s breath comes in short gasps as he lays in an uneasy sleep. Beads of sweat cover his head and a noxious odor rises from his bloody wounds. It would seem that what time he has left will not last for long.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81453, 'enUS', 'A nearby Void Wolf is alerted to you! Get away before it attacks!', 'A nearby Void Wolf is alerted to you! Get away before it attacks!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81454, 'enUS', 'A nearby Void Occulus is alerted to you! Get away before it attacks!', 'A nearby Void Occulus is alerted to you! Get away before it attacks!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81455, 'enUS', 'I have seen your forces marching boldly across the landscape with the confidence of the Warsong.$b$bYou have my attention.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81456, 'enUS', 'Accept the Defection of Gronnstalker Rokash. (Garrison Follower)', 'Accept the  Defection of Gronnstalker Rokash. (Garrison Follower)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81457, 'enUS', 'You take care of the lieutenants, champion. Bladefist is mine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81458, 'enUS', 'Cutting down unarmed prisoners? Why not fight a real battle?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81459, 'enUS', '', '[ph] Can I follow you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81460, 'enUS', 'Accept the Blood Oath of Lokra. (Garrison Follower)', 'Accept the  Blood Oath of Lokra. (Garrison Follower)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81461, 'enUS', 'You are about to be ejected from your Wor\'var Demolisher. Return to Nagrand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81462, 'enUS', 'At your service, commander.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81463, 'enUS', 'Capping', 'Capping', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81464, 'enUS', 'Fracking', 'Fracking', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81465, 'enUS', 'We\'re all counting on you, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81471, 'enUS', '$n crafts an Air Trinket!', '$n crafts an Air Trinket!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81472, 'enUS', '$n is attempting to free an ally from all crowd-controlling effects!', '$n is attempting to free an ally from all crowd-controlling effects!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81473, 'enUS', 'A throng of angry orcs begin throwing |cFFFF0000|Hspell:159386|h[Iron Bombs]|h|r into the arena! $B$B x5 Iron Bombers |TInterface\\Icons\\achievement_character_orc_male_brn.blp:32|t', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81474, 'enUS', 'Drunken Ogres begin hurling |cFFFF0000|Hspell:159410|h[Mauling Brew]|h|r from the stands! $B$B x2 Drunken Bileslingers|TInterface\\Icons\\achievement_reputation_ogre.blp:32|t x5 Iron Bombers |TInterface\\Icons\\achievement_character_orc_male_brn.blp:32|t', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81479, 'enUS', 'Eager for bloodshed, the crowd rains destruction onto the arena floor! $B$B x1 Drunken Bileslinger|TInterface\\Icons\\achievement_reputation_ogre.blp:32|t x5 Iron Bombers |TInterface\\Icons\\achievement_character_orc_male_brn.blp:32|t', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81480, 'enUS', 'Enthralled by the violence, the crowd attempts to up the body count! $B$B x3 Drunken Bileslingers |TInterface\\Icons\\achievement_reputation_ogre.blp:32|t x5 Iron Bombers |TInterface\\Icons\\achievement_character_orc_male_brn.blp:32|t', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81481, 'enUS', 'Enraged by the lack of corpses, the crowd showers the arena with explosives! $B$B x5 Iron Bombers |TInterface\\Icons\\achievement_character_orc_male_brn.blp:32|t', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81482, 'enUS', '', 'Master Velen, I have never led soldiers before. Are you sure--', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81483, 'enUS', 'You are capable of more than you know, Yrel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81485, 'enUS', 'Now go. The Rangari follow your orders. Find the prisoners.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81486, 'enUS', '', 'It\'s hopeless. How are we going to get out of here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81487, 'enUS', 'Think of your people. The captives still need our help!', 'Think of your people. The captives still need our help!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81488, 'enUS', '', 'Why? What is the use of saving them? Only death awaits us all in the end.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81489, 'enUS', 'We can\'t give up now! Heroes learn from their mistakes.', 'We can\'t give up now! Heroes learn from their mistakes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81490, 'enUS', '', 'A hero? Hardly! I am just an initiate, and not a very good one at that.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81491, 'enUS', 'We all start from humble beginnings. I once hunted troggs in the cold mountains of Dun Morogh.', 'We all start from humble beginnings. I once hunted troggs in the cold mountains of Dun Morogh.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81492, 'enUS', '', 'I see, $n. You have never backed down from a challenge. I must strive to do the same.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81493, 'enUS', 'Pull yourself together, Yrel! You can do better than this!', 'Pull yourself together, Yrel! You can do better than this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81494, 'enUS', '', 'Oh! I needed to hear that, $n. I must not fall prey to my own fears.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81495, 'enUS', 'You must choose, Yrel. Will you rise and fight, or fall here to doubt?', 'You must choose, Yrel. Will you rise and fight, or fall here to doubt?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81496, 'enUS', '', 'You are right, $n. My choices do matter. I choose... to fight!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81497, 'enUS', 'Believe in the Light! Despite setbacks, it will always prevail.', 'Believe in the Light! Despite setbacks, it will always prevail.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81498, 'enUS', '', 'Is it true? Good doesn\'t always triumph over evil.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81499, 'enUS', 'The Light is only as strong as its champions. Believe in YOURSELF.', 'The Light is only as strong as its champions. Believe in YOURSELF.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81500, 'enUS', '\'Doubt the Light\' just doesn\'t have the same ring to it.', '\'Doubt the Light\' just doesn\'t have the same ring to it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81501, 'enUS', 'Your people have triumphed over endless persecution. Will you let their hopes die here without a fight?', 'Your people have triumphed over endless persecution. Will you let their hopes die here without a fight?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81502, 'enUS', '', 'You are truly wise, $n. I must believe in myself, for I carry the Light within me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81503, 'enUS', '', 'You choose the strangest times for humor, $c. Nevertheless, you have a point. I must have courage!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81504, 'enUS', '', 'You are right, $n. I will not bow quietly to these enemies. I must fight!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81505, 'enUS', '$n activates the Great Spire\'s Focusing Iris!', '$n activates the Great Spire\'s Focusing Iris!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81506, 'enUS', 'You are about to be ejected from your Telaari Tank. Return to Nagrand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81508, 'enUS', '', 'You have a pretty good shop going on here. Much bigger then the shack I used to work at in Shattrath.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81509, 'enUS', 'Just be ready when our ships arrive at Karabor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81510, 'enUS', 'I\'d like an equipment upgrade...', 'I\'d like an equipment upgrade...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81511, 'enUS', 'I found some strange metal for you to examine...', 'I found some strange metal for you to examine...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81512, 'enUS', 'I have more frost iron ingots for you.', 'I have more frost iron ingots for you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81513, 'enUS', 'Artillery Strike Inbound!', 'Artillery Strike Inbound!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81515, 'enUS', '', 'Keep flappin\' yer gums, if you want to be an orc kabob.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81516, 'enUS', 'You two are workin\' slower than scrawny goblins!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81517, 'enUS', 'Might want to repair that stamper before Northrend thaws!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81518, 'enUS', 'I can\'t wait to hear a job\'s done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81519, 'enUS', '', 'You know how easy it is to hide a body under one of these things?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81520, 'enUS', '', 'That\'s enough outta you, gasbag.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81521, 'enUS', 'Get those peons under control!  You heard Ner\'zhul, no one leaves this village!', 'Get those peons under control!  You heard Ner\'zhul, no one leaves this village!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81522, 'enUS', '|TInterface\\Icons\\spell_shaman_improvedfirenova.blp:20|tThe frequency of the Heart of the Mountain\'s |cFFFF0000|Hspell:155209|h[Blast]|h|r has been increased!', '|TInterface\\Icons\\spell_shaman_improvedfirenova.blp:20|tThe frequency of the Heart of the Mountain\'s |cFFFF0000|Hspell:155209|h[Blast]|h|r has been increased!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81523, 'enUS', 'Invaders have been spotted near the front gate!', 'Invaders have been spotted near the front gate!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81524, 'enUS', 'Invaders have been spotted near the farm!', 'Invaders have been spotted near the farm!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81525, 'enUS', 'Invaders have been spotted near the town hall!', 'Invaders have been spotted near the town hall!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81526, 'enUS', 'Garrison - Event - Orc Attack Stage 1.1a Begin', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81527, 'enUS', 'Garrison - Event - Orc Attack Stage 1.1 Begin', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81528, 'enUS', '', 'Good! We must have arrived before Teron\'gor and Gul\'dan!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81529, 'enUS', '', 'Nyami is working to bolster the defenses against their assault - follow me, we will report to her.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81530, 'enUS', '', 'Beautiful, isn\'t it? Just think - you\'re one of the first outsiders to see inside Auchindoun.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81531, 'enUS', '', 'Strange... why is the door sealed? No matter...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81532, 'enUS', '', 'Let us proceed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81533, 'enUS', '', 'The Vindicators and Exarchs of the past. Heroes all - their souls still remain with us here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81534, 'enUS', '', 'Nyami! We are here -', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81535, 'enUS', '', 'What is this? Oh, no... no...', 26124);
