INSERT INTO `broadcast_text_locale` VALUES (81537, 'enUS', '', '...The protector... still answers her call... you\'ll have to defeat it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81538, 'enUS', '', 'Ahhh!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81539, 'enUS', '', 'She... got away. The defenses...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81540, 'enUS', '', 'She had help. So many of our order were corrupt...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81541, 'enUS', '', 'I\'ll gather any who remain loyal here and protect them. You - please, go put a stop to this madness!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81542, 'enUS', '', 'You... you did it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81543, 'enUS', '', 'Here - this is not enough to thank you for your actions, but it is what we have to give.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81544, 'enUS', '', 'Now we can rebuild... we must rebuild. We will never allow our souls to fall to the Legion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81545, 'enUS', '', 'You have done more than you know. Our people can never thank you enough.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81546, 'enUS', 'Seismic Shockwave! Use a steam vent to get off the ground!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81547, 'enUS', '', 'Now that the Heart of Auchindoun is back inside, our defenses should be impenetrable!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81548, 'enUS', 'Champion! It is good that you have joined us! I will hold these Thunderlord off in case any of your allies arrive.', '', 26124);
