INSERT INTO `broadcast_text_locale` VALUES (81567, 'enUS', 'Thank the Light!', 'Thank the Light!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81568, 'enUS', 'I thought I was going to die here.', 'I thought I was going to die here.', 26124);
