INSERT INTO `broadcast_text_locale` VALUES (81573, 'enUS', 'May your spirit find rest with those of your people\'s ancestors.', 'May your spirit find rest with those of your people\'s ancestors.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81574, 'enUS', 'Kill them. Kill them all.', 'Kill them. Kill them all.', 26124);
