INSERT INTO `broadcast_text_locale` VALUES (81581, 'enUS', '', 'The poor dear, she injured herself trying to escape the flames.$b$b<Vilonia pats Miss Purr-Purr softly on the head.>$b$bLook at that sad face, I can\'t just leave her to face the fire alone.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81582, 'enUS', 'Oregorger has grown hungry and wants to eat more blackrock ore!', '', 26124);
