INSERT INTO `broadcast_text_locale` VALUES (81585, 'enUS', 'Dagg tanks you, friend! <Dagg lets out a guttural chuckle.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81586, 'enUS', '', 'I cannot thank you enough for all of your help, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81587, 'enUS', '', 'Commander! Your timing is impeccable as always.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81588, 'enUS', '', 'Luuka, follow me. It is time we returned to the garrison.', 26124);
