INSERT INTO `broadcast_text_locale` VALUES (81590, 'enUS', 'Ga\'nar said stay out of his way, so I am doing just that. I am not going to get myself killed in the name of the Iron Wolf or his kin.$b$bWe should talk... if you survive this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81591, 'enUS', 'Will you join me in the fight against the Iron Horde?', 'Will you join me in the fight against the Iron Horde?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81592, 'enUS', 'Don\'t mind me. I\'m staying out of this fight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81593, 'enUS', 'Dunno how dem orcs keep catchin\' me. <Dagg snickers disarmingly.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81594, 'enUS', 'Hi again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81595, 'enUS', 'Gonna have to ask demself, \"Did Dagg get catched or did Dagg catched dem?\"\n\nBig hero been blowin\' Dagg\'s cover! But you special, Dagg t\'inks. Dagg is master of dis guys and want to join you.\n\nDagg is unanimous in dis.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81596, 'enUS', 'Unanimously agree with Dagg? (Garrison Follower)', 'Unanimously agree with Dagg? (Garrison Follower)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81597, 'enUS', 'A great leader sets an example for all those who follow.$b$bThey know when to speak, and when to listen.$b$bThey know when to stand and fight for their survival, and when to sacrifice it all for the greater good.$b$bAre you ready to lead, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81598, 'enUS', 'Thank you, stranger. Now I may finally enter Auchindoun.', 'Thank you, stranger. Now I may finally enter Auchindoun.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81599, 'enUS', 'I am free!', 'I am free!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81600, 'enUS', 'A thousand blessings, $c.', 'A thousand blessings, $c.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81601, 'enUS', 'Thank the Light you showed up.', 'Thank the Light you showed up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81602, 'enUS', 'Can you feel it, $n? A great army will stand at this very spot, and they will look to you for guidance.', '', 26124);
