INSERT INTO `broadcast_text_locale` VALUES (81647, 'enUS', 'You want fight in the arena? Talk wit\' Dexyl, he puts the fights together.$b$bMe? I just here to start the match... and clean up the bodies after! BAWHAHA!', '', 26124);
