INSERT INTO `broadcast_text_locale` VALUES (81664, 'enUS', 'Thank you for your assistance, be healed and back to battle!', 'Thank you for your assistance, be healed and back to battle!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81665, 'enUS', 'Forward! We must stop Vorka!', 'Forward! We must stop Vorka!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81666, 'enUS', 'My thanks to you. Be healed and carry on!', 'My thanks to you. Be healed and carry on!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81667, 'enUS', 'We did not expect to find any friendly faces out here in this wasteland. We have survived only by sticking together and relying on one another to get this far.$b$bOur escape attempt was only half executed though. Kor\'gall got wind of our plan before we could assassinate him and take his axe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81668, 'enUS', 'I have never met a beast I could not hunt!', 'I have never met a beast I could not hunt!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81669, 'enUS', '', 'No! Awk! You\'re just another illusion! Another lie! Ra-awk! I\'ll expose the darkness within you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81670, 'enUS', 'Admiral Gar\'an prepares to man the Dreadnaught\'s Main Cannon!', 'Admiral Gar\'an prepares to man the Dreadnaught\'s Main Cannon!', 26124);
