INSERT INTO `broadcast_text_locale` VALUES (81674, 'enUS', 'I am afraid I must depart and rejoin our pack at Wor\'gol. Aka\'Magosh.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81675, 'enUS', 'Out of the way, peon!', 'Out of the way, peon!', 26124);
