INSERT INTO `broadcast_text_locale` VALUES (81677, 'enUS', 'Kash\'drakor says it is time to escape.', 'Kash\'drakor says it is time to escape.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81678, 'enUS', '', 'Who\'s there!? Oh - I\'m glad I\'m not the only one fighting these monsters.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81679, 'enUS', 'The darkness comes for you...', 'The darkness comes for you...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81680, 'enUS', 'More souls for the taking!', 'More souls for the taking!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81681, 'enUS', 'My life for Ner\'zhul!', 'My life for Ner\'zhul!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81682, 'enUS', 'Did my father send you? I told them he would find a way to spring me! Thanks, stranger.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81683, 'enUS', 'You think you\'re clever?! You\'re next to burn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81684, 'enUS', 'Fool! You\'ll need more than flames to finish me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81685, 'enUS', 'Coward! You can\'t hide behind these traps forever!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81686, 'enUS', 'My death changes nothing! Full... ATTACK!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81687, 'enUS', '', 'Up there! The fleet commander shows himself!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81688, 'enUS', 'Brave champion, I need your aid in my defense, we will break this siege if you can give me time!', 'Brave champion, I need your aid in my defense, we will break this siege if you can give me time!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81689, 'enUS', '', 'They\'re breaking through. Retreat! We\'ll make our final stand on the stairs!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81690, 'enUS', 'Commander. I\'m at your disposal.$B$BDo you have any further orders?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81691, 'enUS', 'Oh good, it\'s you, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81692, 'enUS', '', 'How DARE you interrupt my work! I\'ll send your soul screaming into the beyond!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (81693, 'enUS', '$n is hosting a Feast of the Waters. Enjoy!', '$n is hosting a Feast of the Waters. Enjoy!', 26124);
