INSERT INTO `broadcast_text_locale` VALUES (82430, 'enUS', 'I\'ll feed you to the flame.', 'I\'ll feed you to the flame.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82431, 'enUS', 'A fitting sacrifice for Hyperious.', 'A fitting sacrifice for Hyperious.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82432, 'enUS', 'Lava consume you!', 'Lava consume you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82433, 'enUS', 'Hallvalor is under attack. Lok-Narash!', 'Hallvalor is under attack. Lok-Narash!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82434, 'enUS', 'Lok\'tar ogar!', 'Lok\'tar ogar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82435, 'enUS', 'The fire is honor!', 'The fire is honor!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82436, 'enUS', 'My life for the Burning Blade!', 'My life for the Burning Blade!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82437, 'enUS', 'You dare challenge me?', 'You dare challenge me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82438, 'enUS', 'You are without honor.', 'You are without honor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82439, 'enUS', 'Taste my blade.', 'Taste my blade.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82440, 'enUS', 'For the Burning Blade.', 'For the Burning Blade.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82441, 'enUS', 'For honor and glory!', 'For honor and glory!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82442, 'enUS', 'There will be no songs sung for you, $c.', 'There will be no songs sung for you, $c.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82443, 'enUS', 'My blade seeks vengeance.', 'My blade seeks vengeance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82444, 'enUS', 'A blademaster does not fear death, he fears dishonor.', 'A blademaster does not fear death, she fears dishonor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82445, 'enUS', 'An outsider approaches!', 'An outsider approaches!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82446, 'enUS', 'Enter the ring and make your challenge.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82447, 'enUS', 'Commander, shall we see the strength of your forces? You may have the honor of first blood.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82448, 'enUS', 'Ya gonna\' be earnin\' ya titles this trip.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82449, 'enUS', 'Remembah, try to not make the locals too angry. We gotta\' keep a low profile \'til we get fortified.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82450, 'enUS', 'Bring ya scout reports to me, and I gonna be relayin\' dem to the commander.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82451, 'enUS', 'Be watchin\' for any \'ting out of da ordinary.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82452, 'enUS', '', 'Those saberon are going to pay what they owe!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82453, 'enUS', '', 'Find out what happened to that trade shipment, $p. I\'ll make it worth your while!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82454, 'enUS', 'I\'d worry less about the saberon and more about what Pyxni will do to us if we don\'t get her stuff back!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82455, 'enUS', 'There\'s still time to save the others...but you\'ll have to hurry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82456, 'enUS', 'This will be the adventure of a lifetime!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82457, 'enUS', 'Most gladiators have got a mean right or a mean left; Crushmaul... he\'s just mean!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82458, 'enUS', 'Raketalon only eats when she wins in the ring... let\'s just say she\'s well fed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82459, 'enUS', 'You\'ve slain Raketalon and you\'ve crushed Crushmaul! Get in there and do the same thing to Hol\'yelaa! Good luck!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82460, 'enUS', 'Nobody has been able to extinguish Boomspark! He\'s burned through the competition like a thatched roof cottage!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82461, 'enUS', 'This is it, $n! There\'s nobody left to beat after Roakk! Get in there and give \'em hell!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82462, 'enUS', '', 'If you run into any of my lazy workers down there, tell \'em that if they want to get paid they\'d better start working!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82463, 'enUS', 'Do you hear that sound, $p? It\'s the sound of opportunity knocking at your door with a huge pile of money!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82464, 'enUS', 'I am injured, but you must press on!', 'I am injured, but you must press on!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82465, 'enUS', 'Thank you, stranger.', 'Thank you, stranger.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82466, 'enUS', 'My thanks for your aid.', 'My thanks for your aid.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82467, 'enUS', 'My thanks.', 'My thanks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82468, 'enUS', 'This frostwolf is not done yet.', 'This frostwolf is not done yet.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82469, 'enUS', 'I am in your debt.', 'I am in your debt.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82470, 'enUS', 'Lok\'tar friend.', 'Lok\'tar friend.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82471, 'enUS', 'These brutes test our strength!', 'These brutes test our strength!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82472, 'enUS', 'Hmm, nice and thick, and enough to coat the entire pile.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82473, 'enUS', 'This much timber will burn quickly, be ready!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82474, 'enUS', 'I demand vengeance!', 'I demand vengeance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82475, 'enUS', 'Ogre blood will be spilled!', 'Ogre blood will be spilled!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82476, 'enUS', 'There are others, we must save them.', 'There are others, we must save them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82477, 'enUS', 'They will see what a cornered wolf is like!', 'They will see what a cornered wolf is like!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82478, 'enUS', 'Let us slaughter these ogres!', 'Let us slaughter these ogres!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82479, 'enUS', 'My wrists are raw from the chains, but I can fight.', 'My wrists are raw from the chains, but I can fight.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82480, 'enUS', 'The ogres will pay in blood.', 'The ogres will pay in blood.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82481, 'enUS', 'I am weak, but I can follow.', 'I am weak, but I can follow.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82482, 'enUS', 'Lantresor cuts through his chains.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82483, 'enUS', 'You want my things?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82484, 'enUS', 'Those damned Goren, they took out over half of our numbers before we were able to evacuate the mine.$b$bWe\'re holding our own out here but we\'re going to need some serious help if the mine is going to be made safe again.', 'Those damned Goren, they took out over half of our numbers before we were able to evacuate the mine.$b$bWe\'re holding our own out here but we\'re going to need some serious help if the mine is going to be made safe again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82485, 'enUS', 'I\'ll see you at the ring. Fight with honor, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82486, 'enUS', 'I\'ll move forward and scout their gates while you\'re taking out those catapults. Lok\'tar ogar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82487, 'enUS', 'It broke.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82488, 'enUS', 'Maybe we can fix it?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82489, 'enUS', 'I didn\'t break it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82490, 'enUS', 'Magic is scary.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82491, 'enUS', 'Not gonna turn around.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82492, 'enUS', 'Hope it run out of juice soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82493, 'enUS', 'All my stuff in dat tower too...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82494, 'enUS', 'Commander, Over here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82495, 'enUS', 'Now\'s my chance!', 'Now\'s my chance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82496, 'enUS', 'Damn these ogres and their magic! I can\'t find a way to bring the barrier down so we\'re just going to have to sneak in around it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82497, 'enUS', 'I\'ll go first and clear the way, meet me on the other side.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82498, 'enUS', 'A challenge has been issued from this $g man:woman;... this outsider.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82499, 'enUS', 'Bomb Exploded: $9067w (-$8911w)', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82500, 'enUS', 'Finish it. Show us the might of the Warsong.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82501, 'enUS', 'With pleasure.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82502, 'enUS', 'Let\'s finish this, pup.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82503, 'enUS', 'He was only Warsong and did not fight very well in any case.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82504, 'enUS', 'Now you will face a true challenge -- my grandsons.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82505, 'enUS', 'You killed their father outside. It\'s only fitting they return the favor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82506, 'enUS', 'For father! For honor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82507, 'enUS', 'I will cut you down, $pc!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82508, 'enUS', 'My boys. You\'ve destroyed my legacy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82509, 'enUS', 'I\'ll kill you myself. Honor be damned!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82510, 'enUS', 'Dharl. You have no honor. You will die.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82511, 'enUS', '', 'Hear me, Burning Blade! By right of ascension, I, Azuka Bladefury, daughter of Dharl, assume the mantle of warlord.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82512, 'enUS', '', 'You, $pc, may leave unharmed. However, if I see you again, you will die.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82513, 'enUS', '', 'Any clan member who wishes to challenge me may do so.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82514, 'enUS', '', 'As for you, Lantresor of the Blade, you had no right joining this one\'s challenge.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82515, 'enUS', '', 'You remain a half-breed and an exile. You are not worthy of the Burning Blade. Leave and never return.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82516, 'enUS', 'Lantresor spits and throws his banner on the ground.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82517, 'enUS', 'Overloading', 'Overloading', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82518, 'enUS', 'Dran\'ruk, your theft of $g his:her; blueprints brought $g him:her; here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82523, 'enUS', 'How did you find me?', 'How did you find me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82524, 'enUS', 'Come, $pc. Issue your challenge. We will allow you to fight... and die.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82525, 'enUS', 'Blood and thunder, what a battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82530, 'enUS', 'Let\'s kill some ogres!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82531, 'enUS', 'My blade is yours, $n. I will fight by your side until the end of my days.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82532, 'enUS', 'The corral is built. Time to put it to good use and bring justice to Nagrand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82533, 'enUS', 'The tankworks is built. Time to put it to good use and bring justice to Nagrand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82534, 'enUS', 'I\'ve never seen anything so beautiful. It\'s good working with you, $n.$B$BWe\'re going to crush the Warsong!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82535, 'enUS', '', 'I will go with you, hero. A warm welcome awaits us at Embaari Village.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82536, 'enUS', '', 'Your garrison is impressive, hero. Even draenei builders could not work so quickly.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82537, 'enUS', '', 'Hello Mister Taarvos!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82538, 'enUS', '', 'That temple is the home to the Shadowmoon Clan. Their leader is the warlord, Ner\'zhul.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82539, 'enUS', '', 'Gloomshade Grove hides many dangers. If you enter, I will wait for you outside.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82540, 'enUS', '', 'I have a bad feeling about these woods. I\'ll stay here if you want to venture within.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82541, 'enUS', '', 'We are nearing draenei lands. Come now, my sister is waiting for us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82542, 'enUS', '', 'I never thought I\'d come back from the slave camps of Tanaan alive.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82543, 'enUS', '', 'I watched so many of my people die... and I could do nothing to help them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82544, 'enUS', '', 'Wow! I\'ve never seen anyone move like that before.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82545, 'enUS', '', 'What was that? I couldn\'t hear you! Quickly hero, let\'s go before she comes after me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82546, 'enUS', '', 'I will aid you as best I can.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82547, 'enUS', '', 'Impressive.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82548, 'enUS', '', 'In the flesh!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82549, 'enUS', '', 'Let me guess. You\'ve done this before.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82550, 'enUS', '', 'You have a natural gift.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82551, 'enUS', '', 'I can\'t wait to surprise my sister, Samaara. I never thought I would see her again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82552, 'enUS', '', 'Yrel? After the Iron Horde took you, I feared the worst!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82553, 'enUS', '', 'The light granted me strength when I needed it most. It is good to be home.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82554, 'enUS', '', 'Samaara! I\'m back!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82555, 'enUS', 'In the end, all will be consumed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82556, 'enUS', 'It is but a fraction of the Dark Star\'s power.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82557, 'enUS', '', 'We\'ll see about that!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82558, 'enUS', 'The Iron Horde fleet has been sighted off the coast. We\'re calling every defender to the Temple.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82559, 'enUS', '', 'But Ner\'zhul poses an equal threat. What should we do, Prophet?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82560, 'enUS', 'Champion, I need your help to stop the Dark Star\'s arrival. Maraad, can you hold Karabor until we are finished?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82561, 'enUS', 'As long as the defense crystal stands, we will hold.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82562, 'enUS', 'Commander, I will gather the troops from your garrison. Do not delay long, they will need your leadership on the battlefield.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82563, 'enUS', 'The Gordunni have been disconnected from the empire for too long. They really think a wall can stop us?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82564, 'enUS', 'There we go! Let\'s, shall we?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82565, 'enUS', '', 'Let us hope Ga\'nar\'s fighting skills are better than his navigation skills.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82566, 'enUS', 'How we survived this far is a mystery to me. You are quite the field commander.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82567, 'enUS', '', 'Hero, are you with us?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82568, 'enUS', 'Fly with me, hero. We will assist them from above!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82569, 'enUS', 'The purifying light of the naaru is both our hammer AND shield.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82570, 'enUS', 'They are at the very temple gates - fly closer! Hurry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82571, 'enUS', 'Circle back. We must clear the steps.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82572, 'enUS', 'Exarch Akama! Watch your flanks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82573, 'enUS', 'Thank you, old friend!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82574, 'enUS', 'Never again will you orcish hordes darken the light of Karabor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82575, 'enUS', 'Press onward. By the light of the naaru!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82576, 'enUS', 'Carry the light, brothers and sisters!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82577, 'enUS', 'We\'ve done all we can from up here...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82578, 'enUS', 'We did it! We broke the blockade!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82579, 'enUS', 'We\'ve broken the main siege. We should meet with our forces on the ground.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82580, 'enUS', 'These are the last of our forces. We\'ll stay here to hold the temple gates.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82581, 'enUS', 'Champion, I need you to fight your way to the harbor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82582, 'enUS', '', 'I\'ll approach the enemy from the south. Light of the naaru, guide you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82583, 'enUS', '', 'Champion, the naaru has come to aid us. We must defend it while it gathers power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82584, 'enUS', '', 'We\'ve done it! Now to put an end to this assault...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82585, 'enUS', 'Witness the TRUE power of the Iron Horde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82586, 'enUS', 'ENOUGH. Play time is over. I will deal with you myself.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82587, 'enUS', '', 'Up there! The fleet commander shows himself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82588, 'enUS', '', 'What is the naaru doing?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82589, 'enUS', 'It is done. Victory is ours!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82590, 'enUS', '', 'Hero, you fought fearlessly. For that, please accept my thanks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82591, 'enUS', 'I am ready to teleport to the ship.', 'I am ready to teleport to the ship.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82592, 'enUS', 'Greetings, savior of my people!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82593, 'enUS', 'If you are hearing this message, it means that I am no more - but Karabor is safe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82594, 'enUS', 'I owe you an explanation.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82595, 'enUS', 'The naaru that we draenei revere are creatures of pure light.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82596, 'enUS', 'But if their death is near, they lose their essence, collapsing into creatures of shadow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82597, 'enUS', 'When we draenei first arrived on this world, naaru accompanied us. But they could not stop our vessel from crashing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82598, 'enUS', 'The \"Dark Star\" that appeared in the skies above Draenor that terrible night was no celestial body.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82599, 'enUS', 'It was a fallen naaru, ejected from our vessel in moments of panic and terror. Its name was K\'ara.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82600, 'enUS', 'What was once a creature of faith and beauty became a dark and hungering God, a living font of darkness.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82601, 'enUS', 'We owed K\'ara a great debt. Without the naaru\'s aid, my people would never have escaped our homeworld of Argus.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82602, 'enUS', 'To see K\'ara\'s transformation into shadow was a heavy burden to bear. When the naaru returned to us... I did what I had to do.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82603, 'enUS', 'Remember, life is the light, and light is life itself. They may be exchanged, but nothing is ever lost.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82604, 'enUS', '', 'Kitty!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82605, 'enUS', '', 'Mr. Scratchy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82606, 'enUS', '', 'Let\'s play, Scratch.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82607, 'enUS', '', 'Bad kitty.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82608, 'enUS', '', 'Come on, Mr. Scratch. Follow me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82609, 'enUS', '', 'Mommy says you\'re a mean, ol\' kitty. But I don\'t think so.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82610, 'enUS', '', 'I wonder who\'s going to fly in.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82611, 'enUS', 'Get back to work!', 'Get back to work!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82612, 'enUS', '', 'Now nothing will stop the Iron Horde!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82613, 'enUS', 'The Rubble Pile is too close and %s throws over it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82614, 'enUS', '', 'I am sorry Garrosh, I have failed you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82615, 'enUS', '', 'Now you have tasted the blade of the Dragonmaw.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82616, 'enUS', '', 'All who stand against the Iron Horde will fall.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82617, 'enUS', '', 'Are you dead or just pretending, coward?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82618, 'enUS', '', 'You will burn!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82619, 'enUS', '', 'Flames will consume you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82620, 'enUS', '', 'My blade will end you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82621, 'enUS', '', 'And now, you die!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82622, 'enUS', '', 'Have you come to get a front row seat for Stormwind\'s destruction?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82623, 'enUS', 'It\'s not my fault. At least I have chicken.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82624, 'enUS', 'Leerooooy! Jeeeeeenkins!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82625, 'enUS', 'Check and see if it has my shoulders!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82626, 'enUS', 'My Devout shoulders! I knew they were here! My quest is complete.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82627, 'enUS', 'Now time for a little well deserved rest and relaxation! If you need me, I will be back at your garrison.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82628, 'enUS', 'Attuning', 'Attuning', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82629, 'enUS', '', 'Dey kill me huntas. You an me, we gonna have some fun wit dese wolves.', 26124);
