INSERT INTO `broadcast_text_locale` VALUES (82631, 'enUS', 'I am a shaman of the old ways and I care not for the petty conflicts between clans.$b$bThroughout the years many clans have called this valley home, but it has always belonged to the elements first.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82632, 'enUS', '', 'Be sure to check back for more resources later, commander. The peons will keep putting them in this chest while you are away.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82633, 'enUS', '', 'Hush you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82634, 'enUS', 'But, my lady, what about our reserve in the pon-', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82635, 'enUS', 'You got it. One fully armed and operational army of motivated peons coming right up.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82636, 'enUS', 'Chop, Chop boys, these rocks aren\'t gonna blow themselves up!', 'Chop, Chop boys, these rocks aren\'t gonna blow themselves up!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82637, 'enUS', 'No sleeping on the job fellas!', 'No sleeping on the job fellas!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82638, 'enUS', 'At this rate, we might never get paid for this job.  Speed it up!', 'At this rate, we might never get paid for this job.  Speed it up!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82639, 'enUS', 'Hmmm... This looks just about right.', 'Hmmm... This looks just about right.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82640, 'enUS', 'Nope, this won\'t do at all.', 'Nope, this won\'t do at all.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82641, 'enUS', '', 'The Warsong descended on us by surprise. Tensions have been high in Nagrand since the formation of the Iron Horde, but we didn\'t expect this.$B$BLet\'s be clear. There are only two reasons that I\'m helping you, $r.$B$BFirst, your shadow hunter here saved my life, albeit for her own selfish reasons. I certainly don\'t expect to see another sunrise.$B$BSecond, if the Warsong manage to learn how to use Telaar\'s shield, there\'ll be no stopping them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82642, 'enUS', 'I\'m going to enjoy this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82643, 'enUS', '', 'Bring me back any toads you find hibernating in the mud; I\'m sure one of them is my husband.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82644, 'enUS', 'We need to destroy all of these wards before we can use the scroll of mass teleportation we prepared.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82645, 'enUS', '', 'I\'ll have to kiss each toad to break the spell on my husband.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82646, 'enUS', 'Hey honey, I\'m back.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82647, 'enUS', 'Oh not again... Loola, I\'m right here. I\'m not a toad... for Light\'s sake.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82648, 'enUS', 'Varmint...', 'Varmint...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82649, 'enUS', '', 'I don\'t know... seems suspicious. Maybe I should kiss a couple toads just to be sure.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82650, 'enUS', 'Masterful fighting! We need only take out Zulmork and Cromaug now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82651, 'enUS', 'Now to find Cromaug and put an end to his miserable existence.', '', 26124);
