INSERT INTO `broadcast_text_locale` VALUES (82669, 'enUS', 'We Rangari are trained to endure long vigils. I have not moved a single muscle for several hours.', 'We Rangari are trained to endure long vigils. I have not moved a single muscle for several hours.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82670, 'enUS', 'The swamplighters seem especially upset tonight.', 'The swamplighters seem especially upset tonight.', 26124);
