INSERT INTO `broadcast_text_locale` VALUES (82673, 'enUS', 'Hydras are causing quite a bit of chaos. We will eventually need to take care of them to ensure our safety.', 'Hydras are causing quite a bit of chaos. We will eventually need to take care of them to ensure our safety.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82674, 'enUS', '', 'You will find the Rangari Hidden throughout the marsh. Report back after you have spoken to each of them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82675, 'enUS', '', 'Impressive. Thank you for reporting back to me so quickly, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82676, 'enUS', 'Darkness... comes...', '', 26124);
