INSERT INTO `broadcast_text_locale` VALUES (82683, 'enUS', '', 'I don\'t see many visitors all the way out here. It is so very nice to have a guest.$b$bWill you be staying for dinner?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82684, 'enUS', 'Tank ya, mon. Dese Warsong be trixie an\' got de drop on ol\' J\'wan.', '', 26124);
