INSERT INTO `broadcast_text_locale` VALUES (82686, 'enUS', 'I wish to know the truth about Dahaka. What do you know of her death?', 'I wish to know the truth about Dahaka. What do you know of her death?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82687, 'enUS', 'WARLORD BLACKHAND', 'WARLORD BLACKHAND', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82688, 'enUS', 'LEADER OF THE BLACKROCK ORCS', 'LEADER OF THE BLACKROCK ORCS', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82689, 'enUS', 'Intruders approach the keep! To arms!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82690, 'enUS', 'For Morkurk! For the empire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82691, 'enUS', 'Now, where were we?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82692, 'enUS', '', 'Durotan has grown into a great leader. In part, we have you to thank for that.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82693, 'enUS', 'Orgrim\'s forces are formidable. He may have betrayed me, but I will not allow vengeance to force me into folly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82694, 'enUS', 'I live to servo!', '', 26124);
