INSERT INTO `broadcast_text_locale` VALUES (82696, 'enUS', 'Exploring my home world has proven more dangerous than I hoped.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82697, 'enUS', 'I will stop at nothing to prevent Shattrath from falling again. Show these orcs no mercy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82698, 'enUS', '', 'I fear that Maraad is becoming consumed by his anger.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82699, 'enUS', 'Depressing', 'Depressing', 26124);
