INSERT INTO `broadcast_text_locale` VALUES (82703, 'enUS', 'All systems online! Fire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82704, 'enUS', 'Right in the face!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82705, 'enUS', 'Ooo! Had ta hurt.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82706, 'enUS', '', 'Gar\'rok is a fool! I would have shared this power with him!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82707, 'enUS', '', 'I will devour you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82708, 'enUS', '', 'Cower before my true power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82709, 'enUS', '', 'So that poor fool Gar\'rok is dying and wishes to know the truth?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82710, 'enUS', '', 'I remember Dahaka. She was as you see me now...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82711, 'enUS', 'I \'preciate da blessin\', mon. De Lord of de Grave smile upon ya.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82712, 'enUS', 'Dey\'s someting in Oshu\'gun pullin\' on me soul. I gonna hava look.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82713, 'enUS', '', '...until I drained the life from her wretched body!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82714, 'enUS', 'What dis? I be dead!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82715, 'enUS', 'I gonna haunt dese Warsong foreva. Stay away from de voodoo.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82716, 'enUS', 'AHHHHHHHHHHHHHHHHHHHHHHHH!', 'AHHHHHHHHHHHHHHHHHHHHHHHH!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82717, 'enUS', '', 'It be good ta see ya, Commanda. We need ta bless mah dead, stop de shield, and kill every Warsong in sight.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82718, 'enUS', '', 'It\'s a simple matter, really. Just activate the perimeter crystals. Good thing the Warsong aren\'t too bright.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82719, 'enUS', 'I stand with you, Gar\'rok. Rest with honor.', 'I stand with you, Gar\'rok. Rest with honor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82720, 'enUS', 'I\'ll erase you from existence!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82721, 'enUS', 'Taste the power of the Empire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82722, 'enUS', 'I\'ve had enough of your meddling.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82723, 'enUS', 'You are a fool for challenging me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82724, 'enUS', 'I\'ll turn you to dust where you stand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82725, 'enUS', 'You\'ll take the crystal from my quivering corpse, mortal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82726, 'enUS', '<Hooks have been run through his limbs, and a Shattered Hand insignia carved into his beak.$B$BIt seems the orcs found him first.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82727, 'enUS', 'Quickly! We need a barrier to protect against his arcane annihilation!', 'Quickly! We need a barrier to protect against his arcane annihilation!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82728, 'enUS', 'Nooooooooooooooooooo!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82729, 'enUS', 'You wish to see the power of the Empire firsthand, little $r? So be it. I could use some entertainment.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82730, 'enUS', 'This body grows weary, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82731, 'enUS', 'Your forces can handle any remaining ogres. Let\'s get out of here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82732, 'enUS', 'I shall teleport us back.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82733, 'enUS', 'I bring the gift of death to the Alliance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82734, 'enUS', 'The hour belongs to my enemy but I will return when the odds are in my favor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82735, 'enUS', '', 'It\'s time to see what I can do, to test my limits and break through!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82736, 'enUS', '', 'Defeat never bothered me anyway.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82737, 'enUS', 'I\'m interested in fishing these savage lands.', 'I\'m interested in fishing these savage lands.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82738, 'enUS', 'We won\'t go down that easy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82739, 'enUS', 'Down goes Van Rook eeeeeeeee....', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82740, 'enUS', 'Get the peons back to work.', 'Get the peons back to work.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82741, 'enUS', '', 'Gar\'rok, my mate...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82742, 'enUS', '', 'I have stood by you as a breath upon the wind.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82743, 'enUS', '', 'I have watched as you lived with honor and my love has not waned.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82744, 'enUS', 'Tank schematics? Good thinkin\'. I\'ll look \'em over.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82745, 'enUS', '\'Ey, $n. You wanna join in the fun? Come get a hand on this here plunger.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82746, 'enUS', 'Wait for it...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82747, 'enUS', '', 'Rise, husband. Your ancestors call...reunited we shall answer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82748, 'enUS', '', 'I want this shield surrounding all of Telaar, now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82749, 'enUS', '', 'Find the intruder and that damned shadow hunter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82750, 'enUS', '', 'All glory to the Warsong. All glory to the new warlord, Garrosh Hellscream!', 26124);
