INSERT INTO `broadcast_text_locale` VALUES (82755, 'enUS', '', 'Soon, we will crush our enemies. For the Warsong!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82757, 'enUS', '', 'Watch out, hero! We must protect K\'ara at all costs!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82758, 'enUS', '', 'Dey shield is up. Now be da time ta strike, $n. Kill Kargora and get de shield back down.', 26124);
