INSERT INTO `broadcast_text_locale` VALUES (82766, 'enUS', 'The fishing here is like nothing we have ever seen back home. How about helping us get this fishing operation off the ground?', 'The fishing here is like nothing we have ever seen back home. How about helping us get this fishing operation off the ground?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82767, 'enUS', 'You have my thanks once again, commander. These fiends were going to eat me alive. I will meet you back at the garrison.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82768, 'enUS', '%s goes into a frenzy at the sight of its master\'s death.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (82769, 'enUS', 'Beasts! Move your lazy hides!', 'Beasts! Move your lazy hides!', 26124);
