INSERT INTO `broadcast_text_locale` VALUES (84063, 'enUS', 'Whatsh do you want? Can\'t you shee I\'m trying to drinksss here? Pour yourshelf one.$B$BHemetsh, come to Nagrandsss the Alliance and Horde shaid. There\'sh big game to hunt shtill.$B$BBut, when I gots here wasss that what they wantsh me to do? NO!$B$BHemetsh, hunt the Warshong\'s wolvesss. Deprive the orcsh of their mountsss.$B$BThat\'sh no job for Hemet Neshingwary!', '', 26124);
