INSERT INTO `broadcast_text_locale` VALUES (84709, 'enUS', 'I would sooner take a tree branch into combat than this shield....', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84710, 'enUS', 'Good to see you again, $p.$B$B I knew you couldn\'t leave the Adherents to run free - not after witnessing what they did in Aruuna and Veil Shadar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84711, 'enUS', 'You call that a battle axe? It looks more like a cheap hatchet....', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84712, 'enUS', 'The fate of Azeroth depends on you, $n!', 'The fate of Azeroth depends on you, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84713, 'enUS', 'This is it, Commander $n. Judgment day is upon us! We must be victorious!', 'This is it, Commander $n. Judgment day is upon us! We must be victorious!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84714, 'enUS', 'Get us home safely, $n. I have a family I\'d like to see again.', 'Get us home safely, $n. I have a family I\'d like to see again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84715, 'enUS', 'If my death results in success for your mission it will be a death well spent.', 'If my death results in success for your mission it will be a death well spent.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84716, 'enUS', 'It would seem that the gate is being powered by the souls of innocents!', 'It would seem that the gate is being powered by the souls of innocents!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84717, 'enUS', 'I hear that Gul\'dan is here! Is it true, commander?', 'I hear that Gul\'dan is here! Is it true, commander?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84718, 'enUS', 'I\'ve worked my whole life for this one moment. To die for Azeroth is the most glorious of deaths!', 'I\'ve worked my whole life for this one moment. To die for Azeroth is the most glorious of deaths!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84719, 'enUS', '', 'Come, COME! Sit yourself by my fire. Would you like some frogs legs?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84720, 'enUS', 'Your bodies shall feed the bloom. Aren\'t you thankful?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84721, 'enUS', 'Now\'s my chance!', 'Now\'s my chance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84722, 'enUS', 'Run!', 'Run!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84723, 'enUS', 'She\'s dead! I\'m out of here!', 'She\'s dead! I\'m out of here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84724, 'enUS', 'Thank you!', 'Thank you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84725, 'enUS', 'Work it harder! Better! Faster! Stronger!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84726, 'enUS', 'Take the rappelling rope down, commander.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84727, 'enUS', 'You think you are gladiators? I could crush your skulls between my toes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84728, 'enUS', 'Fight harder or I\'ll save you the trouble of dying in the arena and kill you myself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84729, 'enUS', 'I\'ll see you there. Don\'t wait up!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84730, 'enUS', '', 'Go on, I\'ll be fine! I\'ll catch up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84731, 'enUS', 'I am not the traitor here. It was the false prophet Velen who betrayed our people.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84732, 'enUS', 'Do you have supplies for me commander?  Every piece helps the war effort.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84733, 'enUS', '%s was holding a Bloodmaul Cache!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84734, 'enUS', 'More supplies commander? Our soldiers could always use them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84735, 'enUS', 'I\'m going to laugh when you weaklings die in the ring!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84736, 'enUS', 'I serve our true master now. The Legion whose call I once denied.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84737, 'enUS', 'If you try to dance like a butterfly... you\'ll get crushed like one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84738, 'enUS', 'My Lord Sargeras will scorch this pathetic world, as he has countless others.', '', 26124);
