INSERT INTO `broadcast_text_locale` VALUES (84740, 'enUS', 'When you die in there, I\'ll make sure Meatgut puts what\'s left of you to good use!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84741, 'enUS', 'You will eat, breathe, and kill for the glory of the arena!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84742, 'enUS', 'I have sworn the sacrament. I have taken my new name... Socrethar.', '', 26124);
