INSERT INTO `broadcast_text_locale` VALUES (84747, 'enUS', 'Shhh... You\'ll be okay, Nisha.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84748, 'enUS', 'That one almost hit me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84749, 'enUS', 'I didn\'t mean to steal from you, honest!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84750, 'enUS', 'Nobody ever taught me that stealing was wrong! I was misled as a youth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84751, 'enUS', 'You can let me down now, I learned my lesson!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84752, 'enUS', 'Wow, you want to fight in the arena with that aim?', '', 26124);
