INSERT INTO `broadcast_text_locale` VALUES (84754, 'enUS', 'You\'re pretty much the worst archer I\'ve ever seen!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84755, 'enUS', 'Azeroth has been invaded by a vicious enemy that seems bent on world domination.$b$bWe must act quickly if we are to put a stop to the chaos that is unfolding around us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84756, 'enUS', 'I promise never to lie, cheat, or steal ever again! Honest!', '', 26124);
