INSERT INTO `broadcast_text_locale` VALUES (84758, 'enUS', 'The Legion will prevail.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84759, 'enUS', 'There are others at my command. In Shattrath and Auchindoun.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84760, 'enUS', 'Enough! When next we meet, it will be on MY terms.', '', 26124);
