INSERT INTO `broadcast_text_locale` VALUES (84765, 'enUS', 'Gar\'mak is dead. You fought well today. Meet me back at camp.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84766, 'enUS', 'This madness ends now, Gar\'mak!', '', 26124);
