INSERT INTO `broadcast_text_locale` VALUES (84780, 'enUS', 'I am the night!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84781, 'enUS', 'Hopefully the prisoners you saved will have an opportunity to rebuild Nethergarde to its former glory.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84782, 'enUS', 'For the Raven Mother!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84783, 'enUS', '', 'Scrape your knee in the arena? I can fix any injury for the right price!', 26124);
