INSERT INTO `broadcast_text_locale` VALUES (84785, 'enUS', 'Bring those battleplans back to me and we can begin to dismantle this Iron Horde.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84786, 'enUS', 'Come get your burgers! You like sausage? I\'ve got the juiciest clefthoof wieners around!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84787, 'enUS', 'Hmm... it appears the Iron Horde has formed an alliance with the Dreadmaul ogres. This is going to be more difficult than I thought.', '', 26124);
