INSERT INTO `broadcast_text_locale` VALUES (84789, 'enUS', 'Ahhhhhh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84790, 'enUS', '', 'Got someone you need to shank, bludgeon, cleave, or perforate? I\'ve got the right weapon for any job!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84791, 'enUS', 'Charge!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84792, 'enUS', 'Plant the Alliance flags inside Nethergarde, our men will do the rest.', '', 26124);
