INSERT INTO `broadcast_text_locale` VALUES (84794, 'enUS', 'I will meet you at Okril\'lon. We will take down Gar\'mak together.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (84795, 'enUS', 'With Gar\'mak dead the Iron Horde will be in disarray until they appoint a new leader here on Azeroth. Although we have won this battle, I fear the war has just begun.', '', 26124);
