INSERT INTO `broadcast_text_locale` VALUES (85367, 'enUS', 'I can\'t believe we\'re both standing here, $n. It\'s Draenor... but it\'s not Draenor. I feel as though we\'re trespassing on the far side of the looking glass.$b$bLet\'s finish the job and get out of this place, hm?', '', 26124);
